  1â†’// lib/auth/token-manager.ts - Centralized token management system
  2â†’interface TokenData {
  3â†’  token: string;
  4â†’  expiresAt: number;
  5â†’  isRefreshing: boolean;
  6â†’}
  7â†’
  8â†’interface RefreshRequest {
  9â†’  resolve: (token: string | null) => void;
 10â†’  reject: (error: Error) => void;
 11â†’  timestamp: number;
 12â†’}
 13â†’
 14â†’class TokenManager {
 15â†’  private tokenData: TokenData = {
 16â†’    token: '',
 17â†’    expiresAt: 0,
 18â†’    isRefreshing: false,
 19â†’  };
 20â†’  
 21â†’  private refreshQueue: RefreshRequest[] = [];
 22â†’  private refreshPromise: Promise<string | null> | null = null;
 23â†’  private lastRefreshAttempt = 0;
 24â†’  private readonly REFRESH_COOLDOWN = 1000; // 1 second between refresh attempts
 25â†’  private readonly TOKEN_BUFFER = 2 * 60 * 1000; // 2 minutes buffer before expiry
 26â†’
 27â†’  /**
 28â†’   * Gets a valid token, refreshing if necessary
 29â†’   */
 30â†’  async getToken(): Promise<string | null> {
 31â†’    // Check if we have a valid cached token
 32â†’    if (this.isTokenValid()) {
 33â†’      return this.tokenData.token;
 34â†’    }
 35â†’
 36â†’    // If already refreshing, wait for the current refresh
 37â†’    if (this.tokenData.isRefreshing && this.refreshPromise) {
 38â†’      console.log('â³ Token refresh in progress, waiting for completion');
 39â†’      return this.refreshPromise;
 40â†’    }
 41â†’
 42â†’    // Check refresh cooldown to prevent rapid successive refresh attempts
 43â†’    const now = Date.now();
 44â†’    if (now - this.lastRefreshAttempt < this.REFRESH_COOLDOWN) {
 45â†’      console.warn('â° Token refresh on cooldown, using existing token or waiting');
 46â†’      // If we have any token (even if close to expiry), use it during cooldown
 47â†’      if (this.hasAnyToken()) {
 48â†’        return this.tokenData.token;
 49â†’      }
 50â†’      // If no token at all, wait a bit and try again
 51â†’      await new Promise(resolve => setTimeout(resolve, this.REFRESH_COOLDOWN - (now - this.lastRefreshAttempt)));
 52â†’      return this.getToken();
 53â†’    }
 54â†’
 55â†’    // Start refresh process
 56â†’    return this.refreshToken();
 57â†’  }
 58â†’
 59â†’  /**
 60â†’   * Forces a token refresh and clears cache
 61â†’   */
 62â†’  async forceRefresh(): Promise<string | null> {
 63â†’    console.log('ğŸ”„ Forcing token refresh');
 64â†’    this.clearToken();
 65â†’    return this.refreshToken();
 66â†’  }
 67â†’
 68â†’  /**
 69â†’   * Clears the token cache
 70â†’   */
 71â†’  clearToken(): void {
 72â†’    console.log('ğŸ§¹ Clearing token cache');
 73â†’    this.tokenData = {
 74â†’      token: '',
 75â†’      expiresAt: 0,
 76â†’      isRefreshing: false,
 77â†’    };
 78â†’    
 79â†’    // Reject any queued requests
 80â†’    this.rejectQueuedRequests(new Error('Token cache cleared'));
 81â†’  }
 82â†’
 83â†’  /**
 84â†’   * Checks if the current token is valid
 85â†’   */
 86â†’  private isTokenValid(): boolean {
 87â†’    return (
 88â†’      !!this.tokenData.token &&
 89â†’      this.tokenData.expiresAt > Date.now() + this.TOKEN_BUFFER
 90â†’    );
 91â†’  }
 92â†’
 93â†’  /**
 94â†’   * Checks if we have any token at all (regardless of expiry)
 95â†’   */
 96â†’  private hasAnyToken(): boolean {
 97â†’    return !!this.tokenData.token;
 98â†’  }
 99â†’
100â†’  /**
101â†’   * Queues a token request while refresh is in progress
102â†’   */
103â†’  private queueRequest(): Promise<string | null> {
104â†’    return new Promise((resolve, reject) => {
105â†’      // Clean up old requests (older than 30 seconds)
106â†’      const now = Date.now();
107â†’      this.refreshQueue = this.refreshQueue.filter(req => {
108â†’        if (now - req.timestamp > 30000) {
109â†’          req.reject(new Error('Request timeout'));
110â†’          return false;
111â†’        }
112â†’        return true;
113â†’      });
114â†’
115â†’      // Add to queue
116â†’      this.refreshQueue.push({
117â†’        resolve,
118â†’        reject,
119â†’        timestamp: now,
120â†’      });
121â†’    });
122â†’  }
123â†’
124â†’  /**
125â†’   * Performs the actual token refresh
126â†’   */
127â†’  private async refreshToken(): Promise<string | null> {
128â†’    if (this.tokenData.isRefreshing && this.refreshPromise) {
129â†’      return this.refreshPromise;
130â†’    }
131â†’
132â†’    this.tokenData.isRefreshing = true;
133â†’    this.lastRefreshAttempt = Date.now();
134â†’
135â†’    this.refreshPromise = this.performRefresh();
136â†’    
137â†’    try {
138â†’      const token = await this.refreshPromise;
139â†’      this.resolveQueuedRequests(token);
140â†’      return token;
141â†’    } catch (error) {
142â†’      this.rejectQueuedRequests(error as Error);
143â†’      throw error;
144â†’    } finally {
145â†’      this.tokenData.isRefreshing = false;
146â†’      this.refreshPromise = null;
147â†’    }
148â†’  }
149â†’
150â†’  /**
151â†’   * Performs the actual token refresh logic
152â†’   */
153â†’  private async performRefresh(): Promise<string | null> {
154â†’    console.log('ğŸ”„ Starting token refresh');
155â†’
156â†’    // Try Clerk client-side first (fastest)
157â†’    if (typeof window !== 'undefined') {
158â†’      try {
159â†’        // @ts-ignore - Clerk global is available on client
160â†’        const clerk = window.Clerk;
161â†’        if (clerk?.session) {
162â†’          const token = await clerk.session.getToken({ template: 'hasura' });
163â†’          if (token) {
164â†’            this.cacheToken(token);
165â†’            console.log('âœ… Token refreshed via Clerk client');
166â†’            return token;
167â†’          }
168â†’        }
169â†’      } catch (error) {
170â†’        console.warn('âš ï¸ Clerk client refresh failed:', error);
171â†’      }
172â†’    }
173â†’
174â†’    // Fallback to API endpoint
175â†’    try {
176â†’      const response = await fetch('/api/auth/token', {
177â†’        credentials: 'include',
178â†’        headers: {
179â†’          'Cache-Control': 'no-cache',
180â†’        },
181â†’      });
182â†’
183â†’      if (!response.ok) {
184â†’        throw new Error(`Token API returned ${response.status}`);
185â†’      }
186â†’
187â†’      const data = await response.json();
188â†’      const { token, expiresIn = 3600 } = data;
189â†’
190â†’      if (!token) {
191â†’        throw new Error('No token received from API');
192â†’      }
193â†’
194â†’      this.cacheToken(token, expiresIn);
195â†’      console.log('âœ… Token refreshed via API');
