// app/api/payroll-dates/generated/route.ts
import { NextRequest, NextResponse } from "next/server";
import { auth } from "@clerk/nextjs/server";
import { adminApolloClient } from "@/lib/server-apollo-client";
import { gql } from "@apollo/client";
import { addMonths, format } from "date-fns";

// GraphQL mutation for generating payroll dates
const GENERATE_PAYROLL_DATES = gql`
  mutation GeneratePayrollDates(
    $payrollId: uuid!
    $startDate: date!
    $endDate: date!
  ) {
    call_generate_payroll_dates(
      args: {
        payroll_id: $payrollId
        start_date: $startDate
        end_date: $endDate
      }
    ) {
      success
    }
  }
`;

export async function POST(req: NextRequest) {
  try {
    // Check authentication
    const { userId, getToken } = await auth();

    if (!userId) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Check user permissions
    const token = await getToken({ template: "hasura" });
    let userRole = "viewer"; // Default role

    if (token) {
      const tokenParts = token.split(".");
      const payload = JSON.parse(
        Buffer.from(tokenParts[1], "base64").toString()
      );
      const hasuraClaims = payload["https://hasura.io/jwt/claims"];
      userRole = hasuraClaims?.["x-hasura-default-role"] || "viewer";
