// GraphQL mutation to deactivate user (soft delete)
const DEACTIVATE_USER = gql`
  mutation DeactivateUser($id: uuid!, $deactivatedBy: String!) {
    update_users_by_pk(
      pk_columns: { id: $id }
      _set: {
        is_active: false
        is_staff: false
        role: "viewer"
        deactivated_at: "now()"
        deactivated_by: $deactivatedBy
        updated_at: "now()"
      }
    ) {
      id
      name
      email
      role
      clerk_user_id
      is_staff
      is_active
      deactivated_at
      deactivated_by
      manager {
        id
        name
        email
      }
    }
  }
`;

// GraphQL mutation to hard delete user (developers only)
const HARD_DELETE_USER = gql`
  mutation HardDeleteUser($id: uuid!) {
    delete_users_by_pk(id: $id) {
      id
      name
      email
      role
      clerk_user_id
    }
  }

