188â†’export function AuthProvider({ children }: { children: React.ReactNode }) {
189â†’  const {
190â†’    isLoaded: isClerkLoaded,
191â†’    userId,
192â†’    isSignedIn,
193â†’    signOut: clerkSignOut,
194â†’    getToken,
195â†’  } = useAuth();
196â†’  const { user } = useUser();
197â†’  const {
198â†’    currentUser: databaseUser,
199â†’    loading: dbUserLoading,
200â†’    extractionAttempts,
201â†’  } = useCurrentUser();
202â†’  const [userRole, setUserRole] = useState<UserRole>("viewer");
203â†’  const [isRoleLoading, setIsRoleLoading] = useState(true);
204â†’  const [permissions, setPermissions] = useState<string[]>([]);
205â†’  const [hasValidDatabaseUser, setHasValidDatabaseUser] = useState(false);
206â†’  const [validationState, setValidationState] = useState({
207â†’    lastValidationTime: 0,
208â†’    validationInProgress: false,
209â†’    consecutiveFailures: 0,
210â†’  });
211â†’
212â†’  // Database user validation with mutex protection
213â†’  const validateDatabaseUser = useCallback(async () => {
214â†’    if (!isSignedIn || !isClerkLoaded || dbUserLoading) {
215â†’      return;
216â†’    }
217â†’
218â†’    const now = Date.now();
219â†’    
220â†’    // Skip if validation is in progress or too recent
221â†’    if (validationState.validationInProgress || 
222â†’        (now - validationState.lastValidationTime < 3000)) {
223â†’      return;
224â†’    }
225â†’
226â†’    // Skip if too many consecutive failures
227â†’    if (validationState.consecutiveFailures >= 3) {
228â†’      console.warn('ğŸš« Skipping validation due to consecutive failures');
229â†’      return;
230â†’    }
231â†’
232â†’    try {
233â†’      await authMutex.acquire(
234â†’        `user-validation-${userId}-${now}`,
235â†’        'session_validation',
236â†’        async () => {
237â†’          setValidationState(prev => ({
238â†’            ...prev,
239â†’            validationInProgress: true,
240â†’            lastValidationTime: now,
241â†’          }));
242â†’
243â†’          const newValidStatus = !!databaseUser;
244â†’
245â†’          // Only update state if it actually changed
246â†’          if (hasValidDatabaseUser !== newValidStatus) {
247â†’            setHasValidDatabaseUser(newValidStatus);
248â†’          }
249â†’
250â†’          if (newValidStatus && databaseUser) {
251â†’            // If we have a database user, use their role directly
252â†’            const newRole = databaseUser.role as UserRole;
253â†’            if (userRole !== newRole) {
254â†’              setUserRole(newRole);
255â†’            }
256â†’            setIsRoleLoading(false);
257â†’            
258â†’            // Reset failure count on success
259â†’            setValidationState(prev => ({
260â†’              ...prev,
261â†’              consecutiveFailures: 0,
262â†’            }));
263â†’          } else {
264â†’            // Only log warning if we've tried multiple times and still no user
265â†’            if (extractionAttempts >= 2) {
266â†’              console.warn(
267â†’                "ğŸš¨ AuthContext - SECURITY: Authenticated user not found in database after multiple attempts",
268â†’                {
269â†’                  clerkUserId: userId,
270â†’                  userEmail: user?.emailAddresses?.[0]?.emailAddress,
271â†’                  extractionAttempts,
272â†’                }
273â†’              );
274â†’            }
275â†’
276â†’            // If no database user, fall back to JWT role
277â†’            if (!isRoleLoading && !authMutex.hasOperationType('token_refresh')) {
278â†’              await fetchUserRole();
279â†’            }
280â†’            
281â†’            // Increment failure count
282â†’            setValidationState(prev => ({
283â†’              ...prev,
284â†’              consecutiveFailures: prev.consecutiveFailures + 1,
285â†’            }));
286â†’          }
287â†’
288â†’          return true;
289â†’        }
290â†’      );
291â†’    } catch (error) {
292â†’      console.error('âŒ User validation failed:', error);
293â†’      setValidationState(prev => ({
294â†’        ...prev,
295â†’        consecutiveFailures: prev.consecutiveFailures + 1,
296â†’      }));
297â†’    } finally {
298â†’      setValidationState(prev => ({
299â†’        ...prev,
300â†’        validationInProgress: false,
301â†’      }));
302â†’    }
303â†’  }, [
304â†’    isSignedIn,
305â†’    isClerkLoaded,
306â†’    dbUserLoading,
307â†’    databaseUser,
308â†’    userId,
309â†’    user,
310â†’    extractionAttempts,
311â†’    hasValidDatabaseUser,
312â†’    userRole,
313â†’    isRoleLoading,
314â†’    validationState.validationInProgress,
315â†’    validationState.lastValidationTime,
316â†’    validationState.consecutiveFailures,
317â†’  ]);
318â†’
319â†’  // Handle user sign out state
320â†’  const handleSignOutState = useCallback(() => {
321â†’    if (!isSignedIn && isClerkLoaded) {
322â†’      // User signed out - reset state
323â†’      if (hasValidDatabaseUser || userRole !== "viewer") {
324â†’        setHasValidDatabaseUser(false);
325â†’        setUserRole("viewer");
326â†’        setIsRoleLoading(false);
327â†’        setValidationState({
328â†’          lastValidationTime: 0,
329â†’          validationInProgress: false,
330â†’          consecutiveFailures: 0,
331â†’        });
332â†’      }
333â†’    }
334â†’  }, [isSignedIn, isClerkLoaded, hasValidDatabaseUser, userRole]);
335â†’
336â†’  // Monitor database user status
337â†’  useEffect(() => {
338â†’    if (isSignedIn) {
339â†’      validateDatabaseUser();
340â†’    } else {
341â†’      handleSignOutState();
342â†’    }
343â†’  }, [isSignedIn, databaseUser, validateDatabaseUser, handleSignOutState]);
344â†’
345â†’  // Fetch user role from JWT token (only used as fallback) with mutex protection
346â†’  const fetchUserRole = useCallback(async () => {
347â†’    if (!isClerkLoaded || !userId) {
348â†’      setIsRoleLoading(false);
349â†’      return;
350â†’    }
351â†’
352â†’    try {
353â†’      await authMutex.acquire(
354â†’        `role-fetch-${userId}-${Date.now()}`,
355â†’        'token_refresh',
356â†’        async () => {
357â†’          setIsRoleLoading(true);
358â†’          const token = await getToken({ template: "hasura" });
359â†’          if (token) {
360â†’            const payload = JSON.parse(atob(token.split(".")[1]));
361â†’            const claims = payload["https://hasura.io/jwt/claims"];
362â†’            const role = claims?.["x-hasura-default-role"] as UserRole;
363â†’
364â†’            if (role && ROLE_PERMISSIONS[role]) {
365â†’              setUserRole(role);
366â†’            } else {
367â†’              setUserRole("viewer");
368â†’            }
369â†’          } else {
370â†’            setUserRole("viewer");
371â†’          }
372â†’          return true;
373â†’        }
374â†’      );
375â†’    } catch (error) {
376â†’      console.error("Error fetching user role:", error);
377â†’      setUserRole("viewer");
378â†’    } finally {
379â†’      setIsRoleLoading(false);
380â†’    }
381â†’  }, [isClerkLoaded, userId, getToken]);
382â†’
383â†’  // Memoize permissions to prevent unnecessary recalculations
384â†’  const memoizedPermissions = useMemo(() => {
385â†’    return ROLE_PERMISSIONS[userRole] || [];
386â†’  }, [userRole]);
387â†’
388â†’  // Update permissions when memoized permissions change
389â†’  useEffect(() => {
390â†’    if (JSON.stringify(permissions) !== JSON.stringify(memoizedPermissions)) {
391â†’      setPermissions(memoizedPermissions);
392â†’    }
393â†’  }, [memoizedPermissions, permissions]);
394â†’
395â†’  // Permission check functions - STRICT security-aware
396â†’  const hasPermission = (permission: string): boolean => {
397â†’    // SECURITY: ALWAYS deny if not authenticated
398â†’    if (!isSignedIn || !isClerkLoaded) {
399â†’      return false;
400â†’    }
401â†’
402â†’    // SECURITY: ALWAYS deny if user doesn't exist in database
403â†’    if (!hasValidDatabaseUser) {
404â†’      // Log warning for debugging but don't spam
405â†’      if (Math.random() < 0.1) {
406â†’        console.warn(
407â†’          "ğŸš¨ SECURITY: Permission denied - User not found in database",
408â†’          {
409â†’            isSignedIn,
410â†’            hasValidDatabaseUser,
411â†’            userId: userId?.substring(0, 8) + "..." || "none",
412â†’          }
413â†’        );
414â†’      }
415â†’      return false;
416â†’    }
417â†’
418â†’    // SECURITY: Only grant permissions if we have a valid role and permissions
419â†’    if (!userRole || userRole === "viewer") {
420â†’      return permission === "view_dashboard"; // Only allow basic dashboard view
421â†’    }
422â†’
423â†’    return permissions.includes(permission);
424â†’  };
425â†’
426â†’  const hasAnyPermission = (requiredPermissions: string[]): boolean => {
427â†’    // SECURITY: Use the strict hasPermission check for each permission
428â†’    return requiredPermissions.some((permission) => hasPermission(permission));
429â†’  };
430â†’
431â†’  const hasRole = (roles: UserRole[]): boolean => {
432â†’    // SECURITY: ALWAYS deny if not authenticated
433â†’    if (!isSignedIn || !isClerkLoaded) {
434â†’      return false;
435â†’    }
436â†’
437â†’    // SECURITY: ALWAYS deny if user doesn't exist in database
438â†’    if (!hasValidDatabaseUser) {
439â†’      if (Math.random() < 0.1) {
440â†’        console.warn(
441â†’          "ğŸš¨ SECURITY: Role check denied - User not found in database",
442â†’          {
443â†’            requestedRoles: roles,
444â†’            currentRole: userRole,
445â†’            hasValidDatabaseUser,
446â†’          }
447â†’        );
448â†’      }
449â†’      return false;
450â†’    }
451â†’
452â†’    // SECURITY: Only grant roles if we have a valid user role
453â†’    if (!userRole) {
454â†’      return false;
455â†’    }
456â†’
457â†’    return roles.includes(userRole);
458â†’  };
459â†’
460â†’  // Computed properties for common checks - STRICT security
461â†’  const hasAdminAccess: boolean = !!(
462â†’    isSignedIn &&
463â†’    isClerkLoaded &&
464â†’    hasValidDatabaseUser &&
465â†’    userRole &&
466â†’    (userRole === "admin" || userRole === "org_admin")
467â†’  );
468â†’
469â†’  const canManageUsers = hasPermission(PERMISSIONS.MANAGE_USERS);
470â†’  const canManageClients = hasPermission(PERMISSIONS.MANAGE_CLIENTS);
471â†’  const canProcessPayrolls = hasPermission(PERMISSIONS.PROCESS_PAYROLLS);
472â†’  const canViewFinancials = hasPermission(PERMISSIONS.VIEW_FINANCIALS);
473â†’
474â†’  // Enhanced sign out
475â†’  const signOut = async () => {
476â†’    try {
477â†’      await clerkSignOut();
478â†’    } catch (error) {
479â†’      console.error("Error signing out:", error);
480â†’    }
481â†’  };
482â†’
483â†’  // Refresh user data with mutex protection
484â†’  const refreshUserData = useCallback(async () => {
485â†’    if (!userId) return;
486â†’
487â†’    try {
488â†’      await authMutex.acquire(
489â†’        `user-refresh-${userId}-${Date.now()}`,
490â†’        'session_validation',
491â†’        async () => {
492â†’          setIsRoleLoading(true);
493â†’          
494â†’          // Force token refresh using the token manager
495â†’          const token = await tokenManager.forceRefresh();
496â†’          
497â†’          if (token) {
498â†’            const payload = JSON.parse(atob(token.split(".")[1]));
499â†’            const claims = payload["https://hasura.io/jwt/claims"];
500â†’            const role = claims?.["x-hasura-default-role"] as UserRole;
501â†’
502â†’            if (role && ROLE_PERMISSIONS[role]) {
503â†’              setUserRole(role);
504â†’            }
505â†’          }
506â†’          
507â†’          // Reset validation state on successful refresh
508â†’          setValidationState(prev => ({
509â†’            ...prev,
510â†’            consecutiveFailures: 0,
511â†’            lastValidationTime: Date.now(),
512â†’          }));
513â†’          
514â†’          return true;
515â†’        }
516â†’      );
517â†’    } catch (error) {
518â†’      console.error("Error refreshing user data:", error);
519â†’      throw error; // Re-throw for the session handler to catch
520â†’    } finally {
521â†’      setIsRoleLoading(false);
522â†’    }
523â†’  }, [userId]);
524â†’
525â†’  const value: AuthContextType = {
526â†’    // Authentication state
527â†’    isAuthenticated: !!isSignedIn,
528â†’    isLoading: !isClerkLoaded || isRoleLoading,
529â†’    userId: userId || null,
530â†’    userEmail: user?.emailAddresses[0]?.emailAddress || null,
531â†’    userName: user?.fullName || user?.firstName || null,
532â†’
533â†’    // Role and permissions
534â†’    userRole,
535â†’    userPermissions: permissions,
536â†’    hasPermission,
537â†’    hasAnyPermission,
538â†’    hasRole,
539â†’
540â†’    // Admin access checks
541â†’    hasAdminAccess,
542â†’    canManageUsers,
543â†’    canManageClients,
544â†’    canProcessPayrolls,
545â†’    canViewFinancials,
546â†’
547â†’    // Actions
548â†’    signOut,
549â†’    refreshUserData,
550â†’  };
551â†’
552â†’  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
553â†’}
554â†’
555â†’export function useAuthContext() {
556â†’  const context = useContext(AuthContext);
557â†’  if (context === undefined) {
558â†’    throw new Error("useAuthContext must be used within an AuthProvider");
559â†’  }
560â†’  return context;
561â†’}
562â†’
563â†’// Export permissions for use in other components
564â†’export { PERMISSIONS };
565â†’

