"use client";

import React, { createContext, useContext, useState, useEffect, useCallback, useMemo } from "react";
import { useAuth, useUser } from "@clerk/nextjs";
import { useCurrentUser } from "@/hooks/useCurrentUser";
import { authMutex } from "@/lib/auth/auth-mutex";
import { tokenManager } from "@/lib/auth/token-manager";

// Add type declaration for global Clerk object
declare global {
  interface Window {
    Clerk?: {
      session: {
        reload: () => Promise<void>;
        getToken: (options: { template: string }) => Promise<string | null>;
      };
    };
  }
}

export type UserRole =
  | "admin"
  | "org_admin"
  | "manager"
  | "consultant"
  | "viewer";

export interface Permission {
  id: string;
  name: string;
  description: string;
}

export interface AuthContextType {
  // Authentication state
  isAuthenticated: boolean;
  isLoading: boolean;
  userId: string | null;
  userEmail: string | null;
  userName: string | null;

  // Role and permissions
  userRole: UserRole;
  userPermissions: string[];
  hasPermission: (permission: string) => boolean;
  hasAnyPermission: (permissions: string[]) => boolean;
  hasRole: (roles: UserRole[]) => boolean;

  // Admin access checks
  hasAdminAccess: boolean;
  canManageUsers: boolean;
  canManageClients: boolean;
  canProcessPayrolls: boolean;
  canViewFinancials: boolean;

  // Actions
  signOut: () => Promise<void>;
  refreshUserData: () => Promise<void>;
}

// Permission definitions
const PERMISSIONS = {
  // Dashboard
  VIEW_DASHBOARD: "view_dashboard",

  // Staff management
  MANAGE_STAFF: "manage_staff",
  VIEW_STAFF: "view_staff",

  // Client management
  MANAGE_CLIENTS: "manage_clients",
  VIEW_CLIENTS: "view_clients",

  // Payroll operations
  PROCESS_PAYROLLS: "process_payrolls",
  APPROVE_PAYROLLS: "approve_payrolls",
  VIEW_PAYROLLS: "view_payrolls",

  // Financial access
  VIEW_FINANCIALS: "view_financials",
  MANAGE_BILLING: "manage_billing",

  // Reports
  VIEW_REPORTS: "view_reports",
  GENERATE_REPORTS: "generate_reports",

