    fetchPolicy: "no-cache",
  });

  const currentUser = data?.users?.[0];
  if (!currentUser) {
    throw new Error("Current user not found");
  }

  const isDeveloper = currentUser.role === "admin";
  const isAdmin = currentUser.role === "org_admin";
