"use client";

import { useState, useEffect } from "react";
import { useUser } from "@clerk/nextjs";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Shield, ShieldCheck, Smartphone, Key, AlertTriangle } from "lucide-react";
import { toast } from "sonner";
import { useAuthContext } from "@/lib/auth-context";

interface MFAStatus {
  enabled: boolean;
  verified: boolean;
  methods: string[];
}

export function MFASetup() {
  const { user } = useUser();
  const { userRole } = useAuthContext();
  const [mfaStatus, setMfaStatus] = useState<MFAStatus>({
    enabled: false,
    verified: false,
    methods: []
  });
  const [isLoading, setIsLoading] = useState(true);
  const [isEnabling, setIsEnabling] = useState(false);

  const requiresMFA = ["admin", "org_admin"].includes(userRole);

  useEffect(() => {
    checkMFAStatus();
  }, [user]);

  const checkMFAStatus = async () => {
    if (!user) return;

    try {
      setIsLoading(true);

      // Check if user has MFA enabled
      const twoFactorEnabled = user.twoFactorEnabled || false;
      const mfaVerified = user.sessionClaims?.two_factor_verified === true;

      setMfaStatus({
        enabled: twoFactorEnabled,
        verified: mfaVerified,
        methods: twoFactorEnabled ? ["totp"] : []
      });
    } catch (error) {
      console.error("Error checking MFA status:", error);
      toast.error("Failed to check MFA status");
    } finally {
      setIsLoading(false);
    }
  };

  const enableMFA = async () => {
    if (!user) return;

    try {
      setIsEnabling(true);

      // Use Clerk's built-in MFA setup
      // This will open Clerk's MFA setup flow
      await user.createTOTP();

      toast.success("MFA setup initiated. Please follow the instructions.");

      // Refresh status after a delay
      setTimeout(() => {
        checkMFAStatus();
      }, 2000);

    } catch (error) {
      console.error("Error enabling MFA:", error);
      toast.error("Failed to enable MFA. Please try again.");
    } finally {
      setIsEnabling(false);
    }
  };

  const disableMFA = async () => {
    if (!user) return;

    if (requiresMFA) {
      toast.error("MFA cannot be disabled for admin accounts");
      return;
    }

    try {
      setIsEnabling(true);

      // Disable MFA through Clerk
      await user.deleteTOTP();

      toast.success("MFA has been disabled");
      await checkMFAStatus();

    } catch (error) {
