    update_payrolls(
      where: { id: { _in: $payrollIds } }
      _set: {
        status: "Approved"
        approved_at: "now()"
        approved_by: $approvedBy
        updated_at: "now()"
      }
    ) {
      affected_rows
      returning {
        id
        name
        status
        approved_at
        total_amount
      }
    }
  }
`;

