    payrolls(
      where: {
        _and: [
          { created_at: { _gte: $startDate } }
          { created_at: { _lte: $endDate } }
          { status: { _in: ["Approved", "Completed"] } }
        ]
      }
      order_by: { created_at: desc }
    ) {
      id
      name
      status
      total_amount
      created_at
      approved_at
      processed_at
      client {
        id
        name
      }
      primary_consultant {
        id
        name
        email
      }
    }
  }
`;

