// app/api/admin/api-keys/route.ts
import { NextRequest, NextResponse } from "next/server";
import { withAuthAndMFA } from "@/lib/security/mfa-middleware";
import { apiKeyManager } from "@/lib/security/api-signing";
import { soc2Logger, LogLevel, LogCategory, SOC2EventType } from "@/lib/logging/soc2-logger";

// List API keys (admin only)
export const GET = withAuthAndMFA(async (request: NextRequest, context) => {
  try {
    await soc2Logger.log({
      level: LogLevel.INFO,
      category: LogCategory.SYSTEM_ACCESS,
      eventType: SOC2EventType.DATA_VIEWED,
      message: "API keys list accessed",
      userId: context.userId,
      userRole: context.userRole,
      entityType: "api_keys"
    }, request);

    const keys = apiKeyManager.listKeys();

    return NextResponse.json({
      success: true,
      keys: keys.map(key => ({
        ...key,
        // Mask the API key for security
        apiKey: key.apiKey.substring(0, 8) + "..." + key.apiKey.slice(-4),
        fullKey: key.apiKey // Include full key for copying
      }))
    });

  } catch (error) {
    console.error("Error listing API keys:", error);
    return NextResponse.json(
      { error: "Failed to list API keys" },
      { status: 500 }
    );
  }
}, {
  allowedRoles: ["admin"],
  forceMFA: true
});

// Create new API key (admin only)
export const POST = withAuthAndMFA(async (request: NextRequest, context) => {
  try {
    const body = await request.json();
    const { permissions = [] } = body;

    // Generate new key pair
    const { apiKey, apiSecret } = apiKeyManager.generateKeyPair();

    // Store the key with permissions
    apiKeyManager.storeKey(apiKey, apiSecret, permissions);

    await soc2Logger.log({
      level: LogLevel.AUDIT,
      category: LogCategory.CONFIGURATION_CHANGE,
      eventType: SOC2EventType.CONFIG_CHANGED,
      message: "New API key created",
      userId: context.userId,
      userRole: context.userRole,
      entityType: "api_key",
      entityId: apiKey,
      metadata: {
        permissions,
        createdBy: context.userId
      }
    }, request);

    return NextResponse.json({
      success: true,
      apiKey,
      apiSecret,
      permissions,
      warning: "Store the API secret securely. It will not be shown again."
    });

  } catch (error) {
    console.error("Error creating API key:", error);
    return NextResponse.json(
      { error: "Failed to create API key" },
      { status: 500 }
    );
  }
}, {
  allowedRoles: ["admin"],
  forceMFA: true
});

// Revoke API key (admin only)
export const DELETE = withAuthAndMFA(async (request: NextRequest, context) => {
  try {
    const { searchParams } = new URL(request.url);
    const apiKey = searchParams.get("apiKey");

    if (!apiKey) {
      return NextResponse.json(
        { error: "API key is required" },
        { status: 400 }
      );
    }

    // Revoke the key
    apiKeyManager.revokeKey(apiKey);

    await soc2Logger.log({
      level: LogLevel.AUDIT,
      category: LogCategory.CONFIGURATION_CHANGE,
      eventType: SOC2EventType.CONFIG_CHANGED,
      message: "API key revoked",
      userId: context.userId,
      userRole: context.userRole,
      entityType: "api_key",
      entityId: apiKey,
      metadata: {
        revokedBy: context.userId
      }
    }, request);

    return NextResponse.json({
      success: true,
      message: "API key revoked successfully"
    });

  } catch (error) {
    console.error("Error revoking API key:", error);
    return NextResponse.json(
      { error: "Failed to revoke API key" },
      { status: 500 }
    );
  }
}, {
  allowedRoles: ["admin"],
  forceMFA: true
});

