import { NextRequest, NextResponse } from "next/server";
import { adminApolloClient } from "@/lib/server-apollo-client";
import { gql } from "@apollo/client";
import { z } from "zod";
import { withAuth } from "@/lib/api-auth";
import { soc2Logger, LogLevel, LogCategory, SOC2EventType } from "@/lib/logging/soc2-logger";

// Input validation schema
const reportInputSchema = z.object({
  reportType: z.enum(["user_access", "permission_usage", "security_events", "full_compliance"]),
  startDate: z.string(),
  endDate: z.string(),
  includeDetails: z.boolean().optional().default(false),
});

// GraphQL queries for reports
const USER_ACCESS_REPORT = gql`
  query UserAccessReport($startDate: timestamptz!, $endDate: timestamptz!) {
    audit_user_access_summary {
      user_id
      user_name
      email
      role
      is_active
      total_actions
      read_actions
      write_actions
      successful_logins
      failed_logins
      last_activity
      last_login
    }

    audit_audit_log_aggregate(
      where: {
        event_time: { _gte: $startDate, _lte: $endDate }
      }
    ) {
      aggregate {
        count
      }
      nodes {
        action
        resource_type
        success
      }
    }
  }
`;

const PERMISSION_USAGE_REPORT = gql`
  query PermissionUsageReport {
    audit_permission_usage_report {
      role_name
      resource_name
      action
      users_with_permission
      users_who_used_permission
      total_usage_count
      last_used
    }
  }
`;

const SECURITY_EVENTS_REPORT = gql`
  query SecurityEventsReport($startDate: timestamptz!, $endDate: timestamptz!) {
    security_event_log(
      where: {
        created_at: { _gte: $startDate, _lte: $endDate }
      }
      order_by: { severity: desc, created_at: desc }
    ) {
      id
      event_type
      severity
      user_id
      details
      resolved
      created_at
      resolved_at
    }

    security_event_log_aggregate(
      where: {
        created_at: { _gte: $startDate, _lte: $endDate }
      }
    ) {
      aggregate {
        count
      }
      nodes {
        severity
        resolved
      }
    }
  }
`;

export async function POST(request: NextRequest) {
  try {
    // Verify this is coming from Hasura
    const hasuraSecret = request.headers.get("x-hasura-admin-secret");
    if (hasuraSecret !== process.env.HASURA_GRAPHQL_ADMIN_SECRET) {
      return NextResponse.json(
        { success: false, message: "Unauthorized" },
        { status: 401 }
      );
    }

    // Parse and validate input
    const body = await request.json();
    const { input } = body;

    const validatedInput = reportInputSchema.parse(input.input);

    // Convert dates
    const startDate = new Date(validatedInput.startDate).toISOString();
    const endDate = new Date(validatedInput.endDate).toISOString();

    let reportData: any = {};
    let summary: any = {
      reportType: validatedInput.reportType,
      period: {
        start: startDate,
        end: endDate,
      },
      generatedAt: new Date().toISOString(),
    };

    // Generate report based on type
    switch (validatedInput.reportType) {
      case "user_access":
        const userAccessResult = await adminApolloClient.query({
          query: USER_ACCESS_REPORT,
          variables: { startDate, endDate },
        });

        reportData.userAccess = userAccessResult.data.audit_user_access_summary;
        reportData.activitySummary = userAccessResult.data.audit_audit_log_aggregate;

        summary.totalUsers = reportData.userAccess.length;
        summary.activeUsers = reportData.userAccess.filter((u: any) => u.is_active).length;
        summary.totalActions = reportData.activitySummary.aggregate.count;
        break;

