"use client";

import React, { createContext, useContext, useState, useEffect, useCallback, useMemo } from "react";
import { useAuth, useUser } from "@clerk/nextjs";
import { useCurrentUser } from "@/hooks/useCurrentUser";
import { authMutex } from "@/lib/auth/auth-mutex";
import { centralizedTokenManager } from "@/lib/auth/centralized-token-manager";

// Add type declaration for global Clerk object
declare global {
  interface Window {
    Clerk?: {
      session: {
        reload: () => Promise<void>;
        getToken: (options: { template: string }) => Promise<string | null>;
      };
    };
  }
}

export type UserRole =
  | "admin"
  | "org_admin"
  | "manager"
  | "consultant"
  | "viewer";

export interface Permission {
  id: string;
  name: string;
  description: string;
}

export interface AuthContextType {
  // Authentication state
  isAuthenticated: boolean;
  isLoading: boolean;
  userId: string | null;
  userEmail: string | null;
  userName: string | null;

  // Role and permissions
  userRole: UserRole;
  userPermissions: string[];
  hasPermission: (permission: string) => boolean;
  hasAnyPermission: (permissions: string[]) => boolean;
  hasRole: (roles: UserRole[]) => boolean;

  // Admin access checks
  hasAdminAccess: boolean;
  canManageUsers: boolean;
  canManageClients: boolean;
  canProcessPayrolls: boolean;
  canViewFinancials: boolean;

  // Actions
  signOut: () => Promise<void>;
  refreshUserData: () => Promise<void>;
}

// Permission definitions
const PERMISSIONS = {
  // Dashboard
  VIEW_DASHBOARD: "view_dashboard",

  // Staff management
  MANAGE_STAFF: "manage_staff",
  VIEW_STAFF: "view_staff",

  // Client management
  MANAGE_CLIENTS: "manage_clients",
  VIEW_CLIENTS: "view_clients",

  // Payroll operations
  PROCESS_PAYROLLS: "process_payrolls",
  APPROVE_PAYROLLS: "approve_payrolls",
  VIEW_PAYROLLS: "view_payrolls",

  // Financial access
  VIEW_FINANCIALS: "view_financials",
  MANAGE_BILLING: "manage_billing",

  // Reports
  VIEW_REPORTS: "view_reports",
  GENERATE_REPORTS: "generate_reports",

  // System administration
  SYSTEM_ADMIN: "system_admin",
  MANAGE_SETTINGS: "manage_settings",
  MANAGE_ROLES: "manage_roles",

  // User management
  MANAGE_USERS: "manage_users",
  INVITE_USERS: "invite_users",

  // Developer tools
  DEVELOPER_TOOLS: "developer_tools",
} as const;

// Role-permission mapping
const ROLE_PERMISSIONS: Record<UserRole, string[]> = {
  admin: [
    PERMISSIONS.VIEW_DASHBOARD,
    PERMISSIONS.MANAGE_STAFF,
    PERMISSIONS.VIEW_STAFF,
    PERMISSIONS.MANAGE_CLIENTS,
    PERMISSIONS.VIEW_CLIENTS,
    PERMISSIONS.PROCESS_PAYROLLS,
    PERMISSIONS.APPROVE_PAYROLLS,
    PERMISSIONS.VIEW_PAYROLLS,
    PERMISSIONS.VIEW_FINANCIALS,
    PERMISSIONS.MANAGE_BILLING,
    PERMISSIONS.VIEW_REPORTS,
    PERMISSIONS.GENERATE_REPORTS,
    PERMISSIONS.SYSTEM_ADMIN,
    PERMISSIONS.MANAGE_SETTINGS,
    PERMISSIONS.MANAGE_ROLES,
    PERMISSIONS.MANAGE_USERS,
    PERMISSIONS.INVITE_USERS,
    PERMISSIONS.DEVELOPER_TOOLS,
  ],
  org_admin: [
    PERMISSIONS.VIEW_DASHBOARD,
    PERMISSIONS.MANAGE_STAFF,
    PERMISSIONS.VIEW_STAFF,
    PERMISSIONS.MANAGE_CLIENTS,
    PERMISSIONS.VIEW_CLIENTS,
    PERMISSIONS.PROCESS_PAYROLLS,
    PERMISSIONS.APPROVE_PAYROLLS,
    PERMISSIONS.VIEW_PAYROLLS,
    PERMISSIONS.VIEW_FINANCIALS,
    PERMISSIONS.MANAGE_BILLING,
    PERMISSIONS.VIEW_REPORTS,
    PERMISSIONS.GENERATE_REPORTS,
    PERMISSIONS.MANAGE_SETTINGS,
    PERMISSIONS.MANAGE_ROLES,
    PERMISSIONS.MANAGE_USERS,
    PERMISSIONS.INVITE_USERS,
  ],
  manager: [
    PERMISSIONS.VIEW_DASHBOARD,
    PERMISSIONS.MANAGE_STAFF,
    PERMISSIONS.VIEW_STAFF,
    PERMISSIONS.MANAGE_CLIENTS,
    PERMISSIONS.VIEW_CLIENTS,
    PERMISSIONS.PROCESS_PAYROLLS,
    PERMISSIONS.APPROVE_PAYROLLS,
    PERMISSIONS.VIEW_PAYROLLS,
    PERMISSIONS.VIEW_FINANCIALS,
    PERMISSIONS.VIEW_REPORTS,
    PERMISSIONS.GENERATE_REPORTS,
    PERMISSIONS.INVITE_USERS,
  ],
  consultant: [
    PERMISSIONS.VIEW_DASHBOARD,
    PERMISSIONS.VIEW_STAFF,
    PERMISSIONS.VIEW_CLIENTS,
    PERMISSIONS.PROCESS_PAYROLLS,
    PERMISSIONS.VIEW_PAYROLLS,
    PERMISSIONS.VIEW_REPORTS,
  ],
  viewer: [
