    PERMISSIONS.GENERATE_REPORTS,
    PERMISSIONS.SYSTEM_ADMIN,
    PERMISSIONS.MANAGE_SETTINGS,
    PERMISSIONS.MANAGE_ROLES,
    PERMISSIONS.MANAGE_USERS,
    PERMISSIONS.INVITE_USERS,
    PERMISSIONS.DEVELOPER_TOOLS,
