// Legacy support for existing code
export const tokenManager = {
  getToken: () => centralizedTokenManager.getToken(),
  forceRefresh: () => centralizedTokenManager.forceRefresh(),
  clearToken: () => centralizedTokenManager.clearCurrentUserToken(),
  getDebugInfo: () => centralizedTokenManager.getMetrics()
};

