"use client";

import * as React from "react";
import {
  ColumnDef,
  flexRender,
  getCoreRowModel,
  useReactTable,
} from "@tanstack/react-table";
import { useQuery, useMutation, useApolloClient } from "@apollo/client";
import { isAuthError } from "@/lib/apollo-client";
import {
  GET_STAFF_LIST,
  GET_ALL_USERS_LIST,
} from "@/graphql/queries/staff/getStaffList";
