import { ErrorBoundaryWrapper } from "@/components/error-boundary";
import { StaffLoading } from "@/components/ui/loading-states";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { ChevronDown } from "lucide-react";

// Define Staff Type
interface Staff {
  id: string;
  email: string;
  name: string;
  role: string;
  username?: string;
  image?: string;
  is_staff: boolean;
  manager_id?: string;
  clerk_user_id?: string;
  created_at?: string;
  updated_at?: string;
  manager?: {
    name: string;
    id: string;
    email: string;
  };
  leaves?: Array<{
    id: string;
    start_date: string;
    end_date: string;
    leave_type: string;
    reason: string;
    status: string;
  }>;
}

// Define Edit Form Data
interface StaffEditForm {
  name: string;
  email: string;
  username: string;
  role: string;
  manager_id: string;
  is_staff: boolean;
}

// Define Create Staff Form Data
interface CreateStaffForm {
  email: string;
  firstName: string;
  lastName: string;
  role: string;
  is_staff: boolean;
}

// Role name mapping
const roleMapping: Record<string, string> = {
  admin: "Developer",
  org_admin: "Admin",
  manager: "Manager",
  consultant: "Consultant",
  viewer: "Viewer",
};

// Role options for select
const roleOptions = [
  { value: "admin", label: "Developer" },
  { value: "org_admin", label: "Admin" },
  { value: "manager", label: "Manager" },
  { value: "consultant", label: "Consultant" },
  { value: "viewer", label: "Viewer" },
];

// MultiSelect component for filters
interface MultiSelectProps {
  options: Array<{ value: string; label: string }>;
  selected: string[];
  onSelectionChange: (selected: string[]) => void;
  placeholder: string;
  label?: string;
}

function MultiSelect({
  options,
  selected,
  onSelectionChange,
  placeholder,
  label,
}: MultiSelectProps) {
  const [open, setOpen] = React.useState(false);

  const handleToggle = (value: string) => {
    const newSelected = selected.includes(value)
      ? selected.filter((item) => item !== value)
      : [...selected, value];
    onSelectionChange(newSelected);
  };

  const displayText =
    selected.length === 0
      ? placeholder
      : selected.length === 1
      ? options.find((opt) => opt.value === selected[0])?.label || placeholder
      : `${selected.length} selected`;

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between"
        >
          {displayText}
          <ChevronDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-full p-0">
        <div className="max-h-60 overflow-auto">
          {options.map((option) => (
            <div
              key={option.value}
              className="flex items-center space-x-2 p-2 hover:bg-accent cursor-pointer"
              onClick={() => handleToggle(option.value)}
            >
              <Checkbox
                checked={selected.includes(option.value)}
                onChange={() => handleToggle(option.value)}
              />
              <span className="text-sm">{option.label}</span>
            </div>
          ))}
        </div>
      </PopoverContent>
    </Popover>
  );
}

