  const handleCreateStaff = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsCreatingStaff(true);
    setCreateError("");

    try {
      const token = await getToken({ template: "hasura" });
      console.log("ğŸ”‘ Staff page - Token present:", !!token);
      console.log("ğŸ”‘ Staff page - Token length:", token?.length || 0);

      // For non-developers, always create as staff member
      const staffStatus = isDeveloper ? createForm.is_staff : true;

      // Combine first and last name for the API
      const fullName =
        `${createForm.firstName.trim()} ${createForm.lastName.trim()}`.trim();

      console.log("ğŸ“¡ Staff page - Making API request to /api/working-post-test");
      const response = await fetch("/api/working-post-test", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          name: fullName, // API expects 'name' not firstName/lastName
          email: createForm.email,
          role: createForm.role,
          is_staff: staffStatus,
        }),
      });

      console.log("ğŸ“¡ Staff page - Response status:", response.status);
      console.log("ğŸ“¡ Staff page - Response redirected:", response.redirected);
      console.log("ğŸ“¡ Staff page - Response URL:", response.url);

      // Check if the response is a redirect (auth issue)
      if (response.redirected) {
        throw new Error("Authentication required. Please sign in again.");
      }

      // Check if the response is JSON
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        throw new Error(`Unexpected response: ${await response.text()}`);
      }

      const data = await response.json();

      if (!response.ok)
        throw new Error(data.error || "Failed to create staff member");

      toast.success(
        `Staff member ${createForm.firstName} ${createForm.lastName} created successfully!`
      );

      // Refresh the staff list and close modal
      refetch();
      closeCreateModal();
    } catch (err: unknown) {
      if (err instanceof Error) {
        setCreateError(err.message);
        toast.error(`Failed to create staff member: ${err.message}`);
      }
    } finally {
      setIsCreatingStaff(false);
    }
  };

