import { NextRequest, NextResponse } from "next/server";
import { withAuth } from "@/lib/api-auth";
import { soc2Logger, LogLevel, LogCategory, SOC2EventType } from "@/lib/logging/soc2-logger";

// Secure staff creation endpoint - admin only
export const POST = withAuth(async (request: NextRequest, session) => {
  try {
    console.log("ğŸ”§ Secure staff creation called");
    console.log("Request method:", request.method);
    console.log("Request URL:", request.url);
    console.log("User:", session.userId, "Role:", session.role);

    // Log staff creation attempt
    await soc2Logger.log({
      level: LogLevel.AUDIT,
      category: LogCategory.DATA_MODIFICATION,
      eventType: SOC2EventType.USER_CREATED,
      message: "Staff creation operation initiated",
      userId: session.userId,
      userRole: session.role,
      entityType: "staff"
    }, request);

    // Get request body for staff creation
    const body = await request.json();

    // TODO: Implement actual staff creation logic here
    // This would typically:
    // 1. Validate input data
    // 2. Create user in Clerk
    // 3. Create user record in database
    // 4. Send invitation email
    // 5. Log the creation

    const staffData = {
      id: crypto.randomUUID(),
      ...body,
      createdBy: session.userId,
      createdAt: new Date().toISOString()
    };

    // Log successful staff creation
    await soc2Logger.log({
      level: LogLevel.AUDIT,
      category: LogCategory.DATA_MODIFICATION,
      eventType: SOC2EventType.USER_CREATED,
      message: "Staff member created successfully",
      userId: session.userId,
      userRole: session.role,
      entityType: "staff",
      entityId: staffData.id,
      metadata: {
        staffEmail: body.email,
        staffRole: body.role
      }
    }, request);

    return NextResponse.json({
      success: true,
      message: "Staff creation endpoint is secured and working!",
      method: request.method,
      url: request.url,
      user: session.userId,
      role: session.role,
      staffData: staffData
    });
  } catch (error) {
    console.error("Staff creation error:", error);

    await soc2Logger.log({
      level: LogLevel.ERROR,
      category: LogCategory.ERROR,
      eventType: SOC2EventType.USER_CREATED,
      message: "Staff creation failed",
      userId: session.userId,
      userRole: session.role,
      errorDetails: {
        message: error instanceof Error ? error.message : String(error),
        stack: error instanceof Error ? error.stack : undefined
      }
    }, request);

    return NextResponse.json({ 
      error: "Staff creation failed",
      details: error instanceof Error ? error.message : String(error)
    }, { status: 500 });
  }
}, {
  allowedRoles: ["admin", "org_admin"] // Only admins can create staff
});

export async function GET() {
  console.log("GET handler working");
  return new NextResponse("GET working");
}

