// lib/user-sync.ts
import { clerkClient } from "@clerk/nextjs/server";
import { gql } from "@apollo/client";
import { adminApolloClient } from "@/lib/server-apollo-client";

// Define user role hierarchy for permission checking
// These must match the Hasura database enum values exactly
export const USER_ROLES = {
  admin: 5, // Developers - highest level
  org_admin: 4, // Standard Admins
  manager: 3,
  consultant: 2,
  viewer: 1,
} as const;

export type UserRole = keyof typeof USER_ROLES;

// Query to find a user by email
const GET_USER_BY_EMAIL = gql`
  query GetUserByEmail($email: String!) {
    users(where: { email: { _eq: $email } }) {
      id
      clerk_user_id
      role
      name
      email
      is_staff
      manager_id
      image
      created_at
      updated_at
      manager {
        id
        name
        email
        role
      }
    }
  }
`;

// Query to find a user by Clerk ID
const GET_USER_BY_CLERK_ID = gql`
  query GetUserByClerkId($clerkId: String!) {
    users(where: { clerk_user_id: { _eq: $clerkId } }) {
      id
      clerk_user_id
      role
      name
      email

      email
      is_staff
      manager_id
      image
      created_at
      updated_at
      manager {
        id
        name
        email
        role
      }
    }
  }
`;

// Enhanced upsert user mutation
const UPSERT_USER = gql`
  mutation UpsertUser(
    $clerkId: String!
    $name: String!
    $email: String!
    $role: user_role = viewer
    $isStaff: Boolean = false
    $managerId: uuid
    $image: String
  ) {
    insert_users_one(
      object: {
        clerk_user_id: $clerkId
        name: $name
        email: $email
        role: $role
        is_staff: $isStaff
        manager_id: $managerId
        image: $image
      }
      on_conflict: {
        constraint: users_clerk_user_id_key
        update_columns: [name, email, image, updated_at]
      }
    ) {
      id
      name
      email
      role
      clerk_user_id
      is_staff
      manager_id
      image
      created_at
      updated_at
    }
  }
`;

// Update user role mutation
const UPDATE_USER_ROLE = gql`
  mutation UpdateUserRole(
    $id: uuid!
    $role: user_role!
    $managerId: uuid
    $isStaff: Boolean
  ) {
    update_users_by_pk(
      pk_columns: { id: $id }
      _set: {
        role: $role
        manager_id: $managerId
        is_staff: $isStaff
        updated_at: "now()"
      }
    ) {
      id
      name
      email
      role
      is_staff
      manager_id
      updated_at
      manager {
        id
        name
        email
      }
    }
  }
`;

// Update user with Clerk ID
const UPDATE_USER_CLERK_ID = gql`
  mutation UpdateUserClerkId($id: uuid!, $clerkId: String!) {
    update_users_by_pk(
      pk_columns: { id: $id }
      _set: { clerk_user_id: $clerkId, updated_at: "now()" }
    ) {
      id
      name
      email
      role

