178â†’// Enhanced user sync function with better error handling
179â†’export async function syncUserWithDatabase(
180â†’  clerkId: string,
181â†’  name: string,
182â†’  email: string,
183â†’  role: UserRole = "viewer",
184â†’  managerId?: string,
185â†’  imageUrl?: string
186â†’) {
187â†’  try {
188â†’    console.log(`ğŸ”„ Syncing user with database: ${clerkId} (${email})`);
189â†’
190â†’    // Get user data from Clerk to ensure we have the latest imageUrl
191â†’    let clerkImageUrl = imageUrl;
192â†’    if (!clerkImageUrl) {
193â†’      try {
194â†’        const client = await clerkClient();
195â†’        const clerkUser = await client.users.getUser(clerkId);
196â†’        clerkImageUrl = clerkUser.imageUrl;
197â†’      } catch (error) {
198â†’        console.warn("Could not fetch image from Clerk:", error);
199â†’      }
200â†’    }
201â†’
202â†’    // First, check if user exists by Clerk ID
203â†’    const { data: userData, errors: queryErrors } =
204â†’      await adminApolloClient.query({
205â†’        query: GET_USER_BY_CLERK_ID,
206â†’        variables: { clerkId },
207â†’        fetchPolicy: "network-only",
208â†’        errorPolicy: "all",
209â†’      });
210â†’
211â†’    if (queryErrors) {
212â†’      console.warn("GraphQL query errors:", queryErrors);
213â†’    }
214â†’
215â†’    let databaseUser = userData?.users?.[0] || null;
216â†’
217â†’    // If no user found by Clerk ID, check by email
218â†’    if (!databaseUser && email) {
219â†’      console.log(`ğŸ” No user found by Clerk ID, checking by email: ${email}`);
220â†’
221â†’      const { data: emailUserData, errors: emailQueryErrors } =
222â†’        await adminApolloClient.query({
223â†’          query: GET_USER_BY_EMAIL,
224â†’          variables: { email },
225â†’          fetchPolicy: "network-only",
226â†’          errorPolicy: "all",
227â†’        });
228â†’
229â†’      if (emailQueryErrors) {
230â†’        console.warn("Email query errors:", emailQueryErrors);
231â†’      }
232â†’
233â†’      const existingUserByEmail = emailUserData?.users?.[0];
234â†’
235â†’      if (existingUserByEmail) {
236â†’        console.log(
237â†’          `âœ… Found existing user by email, updating with Clerk ID: ${existingUserByEmail.name}`
238â†’        );
239â†’
240â†’        // Update the existing user with the Clerk ID
241â†’        const { data: updateData, errors: updateErrors } =
242â†’          await adminApolloClient.mutate({
243â†’            mutation: UPDATE_USER_CLERK_ID,
244â†’            variables: {
245â†’              id: existingUserByEmail.id,
246â†’              clerkId: clerkId,
247â†’            },
248â†’            errorPolicy: "all",
249â†’          });
250â†’
251â†’        if (updateErrors) {
252â†’          console.error("Clerk ID update errors:", updateErrors);
253â†’          throw new Error(
254â†’            `Failed to update user with Clerk ID: ${updateErrors
255â†’              .map((e) => e.message)
256â†’              .join(", ")}`
257â†’          );
258â†’        }
259â†’
260â†’        databaseUser = updateData?.update_users_by_pk;
261â†’        console.log("âœ… Updated existing user with Clerk ID:", databaseUser);
262â†’      }
263â†’    }
264â†’
265â†’    // If still no user found, create a new one
266â†’    if (!databaseUser && name && email) {
267â†’      console.log(
268â†’        `ğŸ“ Creating new user in database: ${name} (${email}) with role: ${role}`
269â†’      );
270â†’
271â†’      const { data: newUserData, errors: mutationErrors } =
272â†’        await adminApolloClient.mutate({
273â†’          mutation: UPSERT_USER,
274â†’          variables: {
275â†’            clerkId,
276â†’            name,
277â†’            email,
278â†’            role,
279â†’            isStaff: role === "org_admin" || role === "manager",
280â†’            managerId: managerId || null,
281â†’            image: clerkImageUrl || null,
282â†’          },
283â†’          errorPolicy: "all",
284â†’        });
285â†’
286â†’      if (mutationErrors) {
287â†’        console.error("User creation errors:", mutationErrors);
288â†’        throw new Error(
289â†’          `Failed to create user: ${mutationErrors
290â†’            .map((e) => e.message)
291â†’            .join(", ")}`
292â†’        );
293â†’      }
294â†’
295â†’      databaseUser = newUserData?.insert_users_one;
296â†’      console.log("âœ… Created new user in database:", databaseUser);
297â†’    } else if (
298â†’      databaseUser &&
299â†’      clerkImageUrl &&
300â†’      databaseUser.image !== clerkImageUrl
301â†’    ) {
302â†’      // Update existing user's image if it's different
303â†’      console.log(`ğŸ–¼ï¸ Updating user image: ${databaseUser.name}`);
304â†’
