    setLoading(true);
    setError(null);

    try {
      const data = await makeRequest(`/api/users/${id}`);

      if (data.success) {
        const user = data.user;
        setCurrentUser(user);
        return user;
      } else {
        throw new Error(data.error || "Failed to fetch user");
      }
