    details: errors
  }, { status: 400 });
}

const staffInput = validationResult.data;

// Step 1: Create user in Clerk (if invited)
let clerkUser = null;
let clerkUserId = null;

if (staffInput.inviteToClerk) {
  try {
    console.log(`ğŸ” Creating Clerk user for: ${staffInput.email}`);

    const client = await clerkClient();

    // Split name into first and last name
    const nameParts = staffInput.name.trim().split(/\s+/);
    const firstName = nameParts[0] || '';
    const lastName = nameParts.slice(1).join(' ') || '';

    // Create user in Clerk with invitation
    clerkUser = await client.users.createUser({
      emailAddress: [staffInput.email],
      firstName,
      lastName,
      skipPasswordRequirement: true, // User will set password via invitation
      publicMetadata: {
        role: staffInput.role,
        isStaff: staffInput.is_staff,
        managerId: staffInput.managerId,
        invitedBy: session.userId,
        invitedAt: new Date().toISOString()
      },
      privateMetadata: {
        hasuraRole: staffInput.role,
        createdViaStaffEndpoint: true,
        createdBy: session.userId
      }
    });

    clerkUserId = clerkUser.id;
    console.log(`âœ… Created Clerk user: ${clerkUserId}`);

    // Send invitation email
    try {
      await client.invitations.createInvitation({
        emailAddress: staffInput.email,
        redirectUrl: `${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/accept-invitation`,
        publicMetadata: {
          role: staffInput.role,
          invitedBy: session.userId,
          invitationType: 'staff_creation'
        }
      });
      console.log(`ğŸ“§ Sent invitation email to: ${staffInput.email}`);
    } catch (inviteError) {
      console.warn(`âš ï¸ Failed to send invitation email: ${inviteError}`);
      // Don't fail the entire operation if invitation fails
    }

  } catch (clerkError: any) {
    console.error(`âŒ Failed to create Clerk user: ${clerkError}`);

    // Check if user already exists
    if (clerkError.errors?.some((e: any) => e.code === 'form_identifier_exists')) {
      await soc2Logger.log({
        level: LogLevel.WARNING,
        category: LogCategory.DATA_MODIFICATION,
        eventType: SOC2EventType.USER_CREATED,
        message: "Staff creation failed - email already exists in Clerk",
        userId: session.userId,
        userRole: session.role,
        metadata: {
          email: staffInput.email,
          error: 'email_already_exists'
        }
      }, request);

      return NextResponse.json({
        error: "User already exists",
        details: `A user with email ${staffInput.email} already exists in the system`
      }, { status: 409 });
    }

    // For other Clerk errors, continue without Clerk user
    console.log(`âš ï¸ Continuing without Clerk user due to error: ${clerkError.message}`);
  }
}

// Step 2: Create/sync user in database
let databaseUser = null;

try {
  console.log(`ğŸ’¾ Creating database user for: ${staffInput.email}`);

  if (clerkUserId) {
    // Sync with Clerk user
    databaseUser = await syncUserWithDatabase(
      clerkUserId,
      staffInput.name,
      staffInput.email,
      staffInput.role as UserRole,
      staffInput.managerId || undefined,
      clerkUser?.imageUrl
    );
  } else {
    // Create database-only user (no Clerk account)
    const { adminApolloClient } = await import('@/lib/server-apollo-client');
    const { gql } = await import('@apollo/client');

    const CREATE_USER_DB_ONLY = gql`
      mutation CreateUserDbOnly(
        $name: String!
        $email: String!
        $role: user_role!
        $isStaff: Boolean!
        $managerId: uuid
      ) {
        insert_users_one(
          object: {
            name: $name
            email: $email
            role: $role
            is_staff: $isStaff
            manager_id: $managerId
            clerk_user_id: null
          }
        ) {
          id
          name
          email
          role
          is_staff
          manager_id
          created_at
          updated_at
        }
      }
    `;

    const { data, errors } = await adminApolloClient.mutate({
      mutation: CREATE_USER_DB_ONLY,
      variables: {
        name: staffInput.name,
        email: staffInput.email,
        role: staffInput.role,
        isStaff: staffInput.is_staff,
        managerId: staffInput.managerId || null
      }
    });

    if (errors) {
      throw new Error(`Database user creation failed: ${errors.map(e => e.message).join(', ')}`);
    }

    databaseUser = data.insert_users_one;
  }

  console.log(`âœ… Created database user: ${databaseUser?.id}`);

} catch (dbError: any) {
  console.error(`âŒ Failed to create database user: ${dbError}`);

  // If we created a Clerk user but failed to create database user, clean up Clerk user
  if (clerkUserId) {
    try {
      const client = await clerkClient();
      await client.users.deleteUser(clerkUserId);
      console.log(`ğŸ§¹ Cleaned up Clerk user after database failure`);
    } catch (cleanupError) {
      console.error(`âŒ Failed to cleanup Clerk user: ${cleanupError}`);
    }
  }

  await soc2Logger.log({
    level: LogLevel.ERROR,
    category: LogCategory.ERROR,
    eventType: SOC2EventType.USER_CREATED,
    message: "Staff creation failed - database error",
    userId: session.userId,
    userRole: session.role,
    errorDetails: {
      message: dbError.message,
      stack: dbError.stack
    }
  }, request);

  return NextResponse.json({
    error: "Failed to create user in database",
    details: dbError.message
  }, { status: 500 });
}

// Prepare response data
const staffData = {
  id: databaseUser?.id,
  clerkUserId: clerkUserId,
  name: staffInput.name,
  email: staffInput.email,
  role: staffInput.role,
  is_staff: staffInput.is_staff,
  manager_id: staffInput.managerId,
  createdBy: session.userId,
  createdAt: databaseUser?.created_at || new Date().toISOString(),
  hasClerkAccount: !!clerkUserId,
  invitationSent: !!clerkUserId && staffInput.inviteToClerk
};

