import { NextRequest, NextResponse } from "next/server";
import { withAuth } from "@/lib/api-auth";
import { soc2Logger, LogLevel, LogCategory, SOC2EventType } from "@/lib/logging/soc2-logger";
import { clerkClient } from "@clerk/nextjs/server";
import { syncUserWithDatabase, UserRole } from "@/lib/user-sync";
import { z } from "zod";

// Input validation schema
const CreateStaffSchema = z.object({
  name: z.string().min(1, "Name is required").max(100, "Name too long"),
  email: z.string().email("Invalid email address"),
  role: z.enum(["admin", "org_admin", "manager", "consultant", "viewer"]).default("viewer"),
  is_staff: z.boolean().default(true),
  managerId: z.string().uuid().optional().nullable(),
  inviteToClerk: z.boolean().default(true)
});

type CreateStaffInput = z.infer<typeof CreateStaffSchema>;

// Secure staff creation endpoint - admin only
export const POST = withAuth(async (request: NextRequest, session) => {
  try {
    console.log("ğŸ”§ Secure staff creation called");
    console.log("Request method:", request.method);
    console.log("Request URL:", request.url);
    console.log("User:", session.userId, "Role:", session.role);

    // Log staff creation attempt
    await soc2Logger.log({
      level: LogLevel.AUDIT,
      category: LogCategory.DATA_MODIFICATION,
      eventType: SOC2EventType.USER_CREATED,
      message: "Staff creation operation initiated",
      userId: session.userId,
      userRole: session.role,
      entityType: "staff"
    }, request);

    // Get and validate request body
    const body = await request.json();

    // Validate input data
    const validationResult = CreateStaffSchema.safeParse(body);
    if (!validationResult.success) {
      const errors = validationResult.error.errors.map(e => `${e.path.join('.')}: ${e.message}`).join(', ');

      await soc2Logger.log({
        level: LogLevel.WARNING,
        category: LogCategory.DATA_MODIFICATION,
        eventType: SOC2EventType.INVALID_INPUT,
        message: "Staff creation failed - validation error",
        userId: session.userId,
        userRole: session.role,
        metadata: {
          validationErrors: errors,
          requestBody: body
        }
      }, request);

      return NextResponse.json({
        error: "Validation failed",
        details: errors
      }, { status: 400 });
    }

    const staffInput = validationResult.data;

    // Step 1: Create user in Clerk (if invited)
    let clerkUser = null;
    let clerkUserId = null;

    if (staffInput.inviteToClerk) {
      try {
        console.log(`ğŸ” Creating Clerk user for: ${staffInput.email}`);

        const client = await clerkClient();

        // Split name into first and last name
        const nameParts = staffInput.name.trim().split(/\s+/);
        const firstName = nameParts[0] || '';
        const lastName = nameParts.slice(1).join(' ') || '';

        // Create user in Clerk with invitation
        clerkUser = await client.users.createUser({
          emailAddress: [staffInput.email],
          firstName,
          lastName,
          skipPasswordRequirement: true, // User will set password via invitation
          publicMetadata: {
            role: staffInput.role,
            isStaff: staffInput.is_staff,
            managerId: staffInput.managerId,
            invitedBy: session.userId,
            invitedAt: new Date().toISOString()
          },
          privateMetadata: {
            hasuraRole: staffInput.role,
            createdViaStaffEndpoint: true,
            createdBy: session.userId
          }
        });

        clerkUserId = clerkUser.id;
        console.log(`âœ… Created Clerk user: ${clerkUserId}`);

        // Send invitation email
        try {
          await client.invitations.createInvitation({
            emailAddress: staffInput.email,
            redirectUrl: `${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/accept-invitation`,
            publicMetadata: {
              role: staffInput.role,
              invitedBy: session.userId,
              invitationType: 'staff_creation'
            }
          });
          console.log(`ğŸ“§ Sent invitation email to: ${staffInput.email}`);
        } catch (inviteError) {
          console.warn(`âš ï¸ Failed to send invitation email: ${inviteError}`);
