        }

      } catch (clerkError: any) {
        console.error(`âŒ Failed to create Clerk user: ${clerkError}`);

        // Check if user already exists
        if (clerkError.errors?.some((e: any) => e.code === 'form_identifier_exists')) {
          await soc2Logger.log({
            level: LogLevel.WARNING,
            category: LogCategory.DATA_MODIFICATION,
            eventType: SOC2EventType.USER_CREATED,
            message: "Staff creation failed - email already exists in Clerk",
            userId: session.userId,
            userRole: session.role,
            metadata: {
              email: staffInput.email,
              error: 'email_already_exists'
            }
          }, request);

          return NextResponse.json({
            error: "User already exists",
            details: `A user with email ${staffInput.email} already exists in the system`
          }, { status: 409 });
        }

        // For other Clerk errors, continue without Clerk user
        console.log(`âš ï¸ Continuing without Clerk user due to error: ${clerkError.message}`);
      }
    }

    // Step 2: Create/sync user in database
    let databaseUser = null;

    try {
      console.log(`ğŸ’¾ Creating database user for: ${staffInput.email}`);

      if (clerkUserId) {
        // Sync with Clerk user
        databaseUser = await syncUserWithDatabase(
          clerkUserId,
          staffInput.name,
          staffInput.email,
          staffInput.role as UserRole,
          staffInput.managerId || undefined,
          clerkUser?.imageUrl
        );
      } else {
        // Create database-only user (no Clerk account)
        const { adminApolloClient } = await import('@/lib/server-apollo-client');
        const { gql } = await import('@apollo/client');

        const CREATE_USER_DB_ONLY = gql`
          mutation CreateUserDbOnly(
            $name: String!
            $email: String!
            $role: user_role!
            $isStaff: Boolean!
            $managerId: uuid
          ) {
