// Define Staff Type
interface Staff {
  id: string;
  email: string;
  name: string;
  role: string;
  username?: string;
  image?: string;
  is_staff: boolean;
  manager_id?: string;
  clerk_user_id?: string;
  created_at?: string;
  updated_at?: string;
  manager?: {
    name: string;
    id: string;
    email: string;
  };
  leaves?: Array<{
    id: string;
