    // Debug logging to catch invalid IDs
    console.log("ğŸ” confirmDeleteStaff called with staffToDelete:", {
      id: staffToDelete.id,
      name: staffToDelete.name,
      clerk_user_id: staffToDelete.clerk_user_id,
      forceHardDelete,
    });

    const isValidUUID =
