import { auth } from "@clerk/nextjs/server";
import { NextRequest, NextResponse } from "next/server";
import { monitorRequest } from "./security/enhanced-route-monitor";

export interface AuthSession {
  userId: string;
  role: string;
  email?: string;
  sessionClaims?: any;
}

// Role hierarchy for permission checking
const ROLE_HIERARCHY: Record<string, number> = {
  developer: 5,
  org_admin: 4,
  manager: 3,
  consultant: 2,
  viewer: 1,
};

// Check if a user role has permission based on hierarchy
export function hasPermission(userRole: string, requiredRole: string): boolean {
  const userLevel = ROLE_HIERARCHY[userRole] || 0;
  const requiredLevel = ROLE_HIERARCHY[requiredRole] || 999;
  return userLevel >= requiredLevel;
}

// Main authentication middleware
export async function requireAuth(
  request: NextRequest,
  options?: {
    requiredRole?: string;
    allowedRoles?: string[];
  }
): Promise<AuthSession> {
  try {
    const { userId, sessionClaims } = await auth();

    if (!userId) {
      throw new Error("Unauthorized: No active session");
    }

    // Extract role from JWT (handle both V1 and V2 formats)
    const hasuraClaims = sessionClaims?.["https://hasura.io/jwt/claims"] as any;
    const userRole = (
      // JWT V2 format
      (sessionClaims?.metadata as any)?.default_role ||
      (sessionClaims?.metadata as any)?.role ||
      // JWT V1 format
      hasuraClaims?.["x-hasura-default-role"] ||
      hasuraClaims?.["x-hasura-role"] ||
      // Fallback
      (sessionClaims as any)?.role
    ) as string;

    // Debug logging for role extraction
    console.log("üîç Auth Debug (V1/V2 compatible):", {
      userId: userId?.substring(0, 8) + "...",
      jwtVersion: hasuraClaims ? "v1" : (sessionClaims?.metadata ? "v2" : "unknown"),
      hasMetadata: !!sessionClaims?.metadata,
      hasHasuraClaims: !!hasuraClaims,
      v2DefaultRole: (sessionClaims?.metadata as any)?.default_role,
      v1DefaultRole: hasuraClaims?.["x-hasura-default-role"],
      v1Role: hasuraClaims?.["x-hasura-role"],
      finalUserRole: userRole,
      sessionId: sessionClaims?.sid,
    });
    const userEmail = sessionClaims?.email as string;

    if (!userRole) {
      throw new Error("Unauthorized: No role assigned");
    }

    // Check role requirements
    if (options?.requiredRole) {
      if (!hasPermission(userRole, options.requiredRole)) {
        throw new Error(
          `Forbidden: Role '${userRole}' does not have permission. Required: '${options.requiredRole}'`
        );
      }
    }

    if (options?.allowedRoles && !options.allowedRoles.includes(userRole)) {
      throw new Error(
        `Forbidden: Role '${userRole}' is not allowed. Allowed roles: ${options.allowedRoles.join(
          ", "
        )}`
      );
    }

    return {
      userId,
      role: userRole,
      email: userEmail,
      sessionClaims,
    };
  } catch (error: any) {
    console.error("Authentication error:", error.message);
    throw error;
  }
}

// Helper to create unauthorized response
export function unauthorizedResponse(
  message: string = "Unauthorized"
): NextResponse {
  return NextResponse.json(
    {
      error: message,
      code: "UNAUTHORIZED",
    },
    { status: 401 }
  );
}

// Helper to create forbidden response
export function forbiddenResponse(message: string = "Forbidden"): NextResponse {
  return NextResponse.json(
    {
      error: message,
      code: "FORBIDDEN",
    },
    { status: 403 }
  );
}

// Wrapper for API routes that require authentication
export function withAuth(
  handler: (
    request: NextRequest,
    session: AuthSession
  ) => Promise<NextResponse>,
  options?: {
    requiredRole?: string;
    allowedRoles?: string[];
  }
) {
  return async (request: NextRequest): Promise<NextResponse> => {
    const startTime = Date.now();
    console.log("üîê withAuth called for:", request.method, request.url);

    try {
      const session = await requireAuth(request, options);
      console.log("‚úÖ Auth successful, calling handler");

      const response = await handler(request, session);

      // Monitor successful request
      await monitorRequest(request, session.userId, startTime, true);

      return response;
    } catch (error: any) {
      console.log("‚ùå Auth failed:", error.message);

      // Monitor failed request
      await monitorRequest(request, undefined, startTime, false);

      if (error.message.includes("Unauthorized")) {
        return unauthorizedResponse(error.message);
      }
      if (error.message.includes("Forbidden")) {
        return forbiddenResponse(error.message);
      }
      // Generic error
      return NextResponse.json(
        {
          error: "Internal server error",
          message: error.message,
        },
        { status: 500 }
      );
    }
  };
}

// Validate webhook signatures for secure webhook endpoints
export async function validateWebhookSignature(
  request: NextRequest,
  secret: string
): Promise<boolean> {
  try {
    const signature = request.headers.get("x-webhook-signature");
    if (!signature) {
      return false;
    }

    const payload = await request.text();
    const crypto = require("crypto");
    const expectedSignature = crypto
      .createHmac("sha256", secret)
      .update(payload)
      .digest("hex");

    return signature === expectedSignature;
  } catch (error) {
    console.error("Webhook signature validation error:", error);
    return false;
  }
}

// Validate cron job requests
export function validateCronRequest(request: NextRequest): boolean {
  const cronSecret = request.headers.get("x-cron-secret");
  const expectedSecret = process.env.CRON_SECRET;

  if (!expectedSecret) {
    console.error("CRON_SECRET not configured");
    return false;
  }

  return cronSecret === expectedSecret;
}

// Rate limiting helper (simple in-memory implementation)
const rateLimitMap = new Map<string, { count: number; resetTime: number }>();

export function checkRateLimit(
  identifier: string,
  limit: number = 100,
  windowMs: number = 60000 // 1 minute
): boolean {
  const now = Date.now();
  const userLimit = rateLimitMap.get(identifier);

  if (!userLimit || userLimit.resetTime < now) {
    rateLimitMap.set(identifier, {
      count: 1,
      resetTime: now + windowMs,
    });
    return true;
  }

  if (userLimit.count >= limit) {
    return false;
  }

  userLimit.count++;
  return true;
}

// Clean up old rate limit entries periodically
setInterval(() => {
  const now = Date.now();
  const keysToDelete: string[] = [];

  rateLimitMap.forEach((value, key) => {
    if (value.resetTime < now) {
      keysToDelete.push(key);
    }
  });

  keysToDelete.forEach(key => rateLimitMap.delete(key));
}, 60000); // Clean up every minute

// middleware.ts - Enhanced route protection with MFA enforcement
import { clerkMiddleware, createRouteMatcher } from "@clerk/nextjs/server";
import { NextResponse } from "next/server";

// ================================
// ROUTE MATCHERS
// ================================

// Public routes that don't require authentication
const isPublicRoute = createRouteMatcher([
  "/",
  "/sign-in(.*)",
  "/accept-invitation(.*)",
  "/api/clerk-webhooks(.*)",
  // Test routes (development only)
  ...(process.env.NODE_ENV === "development" ? [
    "/api/simple-test",
    "/api/debug-post", 
    "/api/minimal-post-test",
    "/api/working-post-test",
    "/api/test-get-public",
    "/api/test-minimal",
    "/api/test-create",
    "/api/test-direct-auth"
  ] : []),
  "/_next(.*)",
  "/favicon.ico",
]);

// ================================
// ENHANCED MIDDLEWARE
// ================================

export default clerkMiddleware(async (auth, req) => {
  // Skip authentication for public routes
  if (isPublicRoute(req)) {
    return NextResponse.next();
  }

  // Protect all non-public routes
  await auth.protect();
  return NextResponse.next();
});

// ================================
// MIDDLEWARE CONFIGURATION
// ================================

export const config = {
  matcher: [
    // Skip Next.js internals and all static files
    "/((?!_next|[^?]*\\.(?:html?|css|js(?!on)|jpe?g|webp|png|gif|svg|ttf|woff2?|ico|csv|docx?|xlsx?|zip|webmanifest)).*)",
    // Always run for API routes
    "/(api|trpc)(.*)",
  ],
};

/**
 * Security Configuration
 * Centralised security settings for the application
 * Compliant with SOC2 and security best practices
 */

export const securityConfig = {
  // Authentication settings
  auth: {
    sessionTimeout: 30 * 60 * 1000, // 30 minutes
    refreshTokenExpiry: 7 * 24 * 60 * 60 * 1000, // 7 days
    maxLoginAttempts: 5,
    lockoutDuration: 15 * 60 * 1000, // 15 minutes
    mfaRequired: false, // MFA enforcement disabled (feature flag)
    mfaEnabled: process.env.FEATURE_MFA_ENABLED === "true", // Feature flag for MFA
    passwordPolicy: {
      minLength: 12,
      requireUppercase: true,
      requireLowercase: true,
      requireNumbers: true,
      requireSpecialChars: true,
      preventReuse: 5, // Prevent reuse of last 5 passwords
      maxAge: 90 * 24 * 60 * 60 * 1000, // 90 days
    },
  },

  // Session management
  session: {
    cookieName: "payroll-session",
    secure: true, // HTTPS only
    httpOnly: true,
    sameSite: "strict" as const,
    maxAge: 30 * 60 * 1000, // 30 minutes
    rolling: true, // Extend session on activity
  },

  // CORS settings
  cors: {
    origin: process.env.ALLOWED_ORIGINS?.split(",") || [
      "http://localhost:3000",
    ],
    credentials: true,
    methods: ["GET", "POST", "PUT", "DELETE", "OPTIONS"],
    allowedHeaders: ["Content-Type", "Authorization", "X-Request-ID"],
    exposedHeaders: ["X-Request-ID"],
    maxAge: 86400, // 24 hours
  },

  // Rate limiting
  rateLimit: {
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: 100, // Limit each IP to 100 requests per windowMs
    message: "Too many requests from this IP, please try again later.",
    standardHeaders: true,
    legacyHeaders: false,
    // Different limits for different endpoints
    endpoints: {
      "/api/auth/login": { windowMs: 15 * 60 * 1000, max: 5 },
      "/api/auth/register": { windowMs: 60 * 60 * 1000, max: 3 },
      "/api/auth/reset-password": { windowMs: 60 * 60 * 1000, max: 3 },
      "/api/export": { windowMs: 60 * 60 * 1000, max: 10 },
    },
  },

  // Content Security Policy
  csp: {
    directives: {
      defaultSrc: ["'self'"],
      scriptSrc: [
        "'self'",
        "'unsafe-inline'",
        "'unsafe-eval'",
        "https://cdn.jsdelivr.net",
      ],
      styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
      fontSrc: ["'self'", "https://fonts.gstatic.com"],
      imgSrc: ["'self'", "data:", "https:", "blob:"],
      connectSrc: ["'self'", process.env.NEXT_PUBLIC_HASURA_GRAPHQL_URL || ""],
      mediaSrc: ["'none'"],
      objectSrc: ["'none'"],
      frameSrc: ["'none'"],
      workerSrc: ["'self'", "blob:"],
      childSrc: ["'none'"],
      formAction: ["'self'"],
      frameAncestors: ["'none'"],
      baseUri: ["'self'"],
      manifestSrc: ["'self'"],
      upgradeInsecureRequests: [],
    },
  },

  // Security headers
  headers: {
    "X-Frame-Options": "DENY",
    "X-Content-Type-Options": "nosniff",
    "X-XSS-Protection": "1; mode=block",
    "Referrer-Policy": "strict-origin-when-cross-origin",
    "Permissions-Policy": "camera=(), microphone=(), geolocation=()",
    "Strict-Transport-Security": "max-age=31536000; includeSubDomains; preload",
  },

  // Input validation
  validation: {
    maxRequestSize: "10mb",
    maxFileSize: 5 * 1024 * 1024, // 5MB
    allowedFileTypes: [
      "application/pdf",
      "image/jpeg",
      "image/png",
      "text/csv",
      "application/vnd.ms-excel",
      "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
    ],
    sanitizeOptions: {
      allowedTags: [],
      allowedAttributes: {},
      disallowedTagsMode: "discard",
    },
  },

  // Encryption settings
  encryption: {
    algorithm: "aes-256-gcm",
    keyDerivation: "pbkdf2",
    iterations: 100000,
    saltLength: 32,
    tagLength: 16,
    ivLength: 16,
  },

  // Audit logging
  audit: {
    enabled: true,
    logLevel: "info",
    sensitiveFields: [
      "password",
      "token",
      "secret",
      "key",
      "authorization",
      "cookie",
      "session",
      "creditCard",
      "ssn",
      "taxId",
      "bankAccount",
    ],
    retentionDays: 2555, // 7 years for SOC2 compliance
  },

  // API security
  api: {
    requireAuthentication: true,
    requireHttps: process.env.NODE_ENV === "production",
    apiKeyHeader: "X-API-Key",
    signatureHeader: "X-Signature",
    timestampHeader: "X-Timestamp",
    timestampTolerance: 5 * 60 * 1000, // 5 minutes
  },

  // Data classification
  dataClassification: {
    levels: {
      CRITICAL: {
        name: "Critical",
        description: "Highly sensitive data requiring maximum protection",
        encryptionRequired: true,
        auditRequired: true,
        accessRestricted: true,
      },
      HIGH: {
        name: "High",
        description: "Sensitive data requiring strong protection",
        encryptionRequired: true,
        auditRequired: true,
        accessRestricted: true,
      },
      MEDIUM: {
        name: "Medium",
        description: "Internal data requiring standard protection",
        encryptionRequired: false,
        auditRequired: true,
        accessRestricted: false,
      },
      LOW: {
        name: "Low",
        description: "Public or non-sensitive data",
        encryptionRequired: false,
        auditRequired: false,
        accessRestricted: false,
      },
    },
  },
};

