300‚Üí          return true;
301‚Üí        }
302‚Üí      );
303‚Üí    } catch (error) {
304‚Üí      console.error("‚ùå User validation failed:", error);
305‚Üí      setValidationState((prev) => ({
306‚Üí        ...prev,
307‚Üí        consecutiveFailures: prev.consecutiveFailures + 1,
308‚Üí      }));
309‚Üí    } finally {
