371‚Üí          if (token) {
372‚Üí            const payload = JSON.parse(atob(token.split(".")[1]));
373‚Üí            const claims = payload["https://hasura.io/jwt/claims"];
374‚Üí            const role = claims?.["x-hasura-default-role"] as UserRole;
375‚Üí
376‚Üí            if (role && ROLE_PERMISSIONS[role]) {
377‚Üí              setUserRole(role);
378‚Üí            } else {
379‚Üí              setUserRole("viewer");
380‚Üí            }
381‚Üí          } else {
382‚Üí            setUserRole("viewer");
383‚Üí          }
384‚Üí          return true;
385‚Üí        }
386‚Üí      );
387‚Üí    } catch (error) {
