 75‚Üí  UNAUTHORIZED_ACCESS = "UNAUTHORIZED_ACCESS",
 76‚Üí  SUSPICIOUS_ACTIVITY = "SUSPICIOUS_ACTIVITY",
 77‚Üí  RATE_LIMIT_EXCEEDED = "RATE_LIMIT_EXCEEDED",
 78‚Üí  INVALID_INPUT = "INVALID_INPUT",
 79‚Üí  SECURITY_SCAN = "SECURITY_SCAN",
 80‚Üí  
 81‚Üí  // Compliance (CC7.3)
 82‚Üí  COMPLIANCE_CHECK = "COMPLIANCE_CHECK",
 83‚Üí  AUDIT_TRAIL_ACCESSED = "AUDIT_TRAIL_ACCESSED",
 84‚Üí  RETENTION_POLICY_APPLIED = "RETENTION_POLICY_APPLIED",
 85‚Üí}
 86‚Üí
 87‚Üí// Log entry interface
 88‚Üíexport interface SOC2LogEntry {
 89‚Üí  level: LogLevel;
