154‚Üíexport class SOC2Logger {
155‚Üí  private static instance: SOC2Logger;
156‚Üí  private buffer: SOC2LogEntry[] = [];
157‚Üí  private flushInterval: NodeJS.Timeout | null = null;
158‚Üí  private readonly maxBufferSize = 100;
159‚Üí  private readonly flushIntervalMs = 5000;
160‚Üí
161‚Üí  private constructor() {
162‚Üí    // Start buffer flush interval
163‚Üí    this.startFlushInterval();
164‚Üí  }
165‚Üí
166‚Üí  static getInstance(): SOC2Logger {
167‚Üí    if (!SOC2Logger.instance) {
168‚Üí      SOC2Logger.instance = new SOC2Logger();
169‚Üí    }
170‚Üí    return SOC2Logger.instance;
171‚Üí  }
172‚Üí
173‚Üí  /**
174‚Üí   * Main logging method
175‚Üí   */
176‚Üí  async log(entry: SOC2LogEntry, request?: NextRequest): Promise<void> {
177‚Üí    try {
178‚Üí      // Enrich entry with request data
179‚Üí      if (request) {
180‚Üí        entry.ipAddress = this.getClientIP(request);
181‚Üí        entry.userAgent = request.headers.get("user-agent") || undefined;
182‚Üí        entry.requestId = request.headers.get("x-request-id") || crypto.randomUUID();
183‚Üí        entry.traceId = request.headers.get("x-trace-id") || undefined;
184‚Üí      }
185‚Üí
186‚Üí      // Add timestamp
187‚Üí      const timestamp = new Date().toISOString();
188‚Üí
189‚Üí      // Determine which table to write to based on category
190‚Üí      switch (entry.category) {
191‚Üí        case LogCategory.AUTHENTICATION:
192‚Üí        case LogCategory.AUTHORIZATION:
193‚Üí          await this.logAuthEvent(entry, timestamp);
194‚Üí          break;
195‚Üí          
196‚Üí        case LogCategory.DATA_ACCESS:
197‚Üí          await this.logDataAccess(entry, timestamp);
198‚Üí          break;
199‚Üí          
200‚Üí        case LogCategory.SECURITY_EVENT:
201‚Üí          await this.logSecurityEvent(entry, timestamp);
202‚Üí          break;
203‚Üí          
204‚Üí        default:
205‚Üí          await this.logAuditEvent(entry, timestamp);
206‚Üí      }
207‚Üí
208‚Üí      // Also log to console in development
209‚Üí      if (process.env.NODE_ENV === "development") {
210‚Üí        this.consoleLog(entry, timestamp);
211‚Üí      }
212‚Üí
213‚Üí      // Check for critical events that need immediate attention
214‚Üí      if (entry.level === LogLevel.CRITICAL || entry.level === LogLevel.SECURITY) {
215‚Üí        await this.handleCriticalEvent(entry);
216‚Üí      }
217‚Üí    } catch (error) {
218‚Üí      // Never fail silently - log to console as fallback
219‚Üí      console.error("[SOC2Logger] Failed to log entry:", error, entry);
220‚Üí      
221‚Üí      // Add to buffer for retry
222‚Üí      this.buffer.push(entry);
223‚Üí      
224‚Üí      // Flush buffer if it's getting full
225‚Üí      if (this.buffer.length >= this.maxBufferSize) {
226‚Üí        await this.flushBuffer();
227‚Üí      }
228‚Üí    }
229‚Üí  }
230‚Üí
231‚Üí  /**
232‚Üí   * Log authentication/authorization events
233‚Üí   */
234‚Üí  private async logAuthEvent(entry: SOC2LogEntry, timestamp: string): Promise<void> {
235‚Üí    const authEvent = {
236‚Üí      event_time: timestamp,
237‚Üí      event_type: entry.eventType,
238‚Üí      user_id: entry.userId,
239‚Üí      user_email: entry.userEmail,
240‚Üí      ip_address: entry.ipAddress,
241‚Üí      user_agent: entry.userAgent,
242‚Üí      success: !entry.errorDetails,
243‚Üí      failure_reason: entry.errorDetails?.message,
244‚Üí      metadata: {
245‚Üí        ...entry.metadata,
246‚Üí        session_id: entry.sessionId,
247‚Üí        request_id: entry.requestId,
248‚Üí        trace_id: entry.traceId,
249‚Üí      },
250‚Üí    };
251‚Üí
252‚Üí    await adminApolloClient.mutate({
253‚Üí      mutation: INSERT_AUTH_EVENT,
