256‚Üí  }
257‚Üí
258‚Üí  /**
259‚Üí   * Log data access events
260‚Üí   */
261‚Üí  private async logDataAccess(entry: SOC2LogEntry, timestamp: string): Promise<void> {
262‚Üí    const dataAccessLog = {
263‚Üí      accessed_at: timestamp,
264‚Üí      user_id: entry.userId,
265‚Üí      resource_type: entry.entityType,
266‚Üí      resource_id: entry.entityId,
267‚Üí      access_type: entry.eventType,
268‚Üí      data_classification: entry.metadata?.dataClassification || "MEDIUM",
269‚Üí      fields_accessed: entry.metadata?.fields,
270‚Üí      query_executed: entry.metadata?.query,
271‚Üí      row_count: entry.metadata?.rowCount,
272‚Üí      ip_address: entry.ipAddress,
273‚Üí      session_id: entry.sessionId,
274‚Üí      metadata: entry.metadata,
275‚Üí    };
276‚Üí
277‚Üí    await adminApolloClient.mutate({
278‚Üí      mutation: INSERT_DATA_ACCESS_LOG,
279‚Üí      variables: { object: dataAccessLog },
280‚Üí    });
281‚Üí  }
282‚Üí
283‚Üí  /**
284‚Üí   * Log security events
285‚Üí   */
286‚Üí  private async logSecurityEvent(entry: SOC2LogEntry, timestamp: string): Promise<void> {
287‚Üí    const severity = this.mapLogLevelToSeverity(entry.level);
288‚Üí    
289‚Üí    const securityEvent = {
290‚Üí      event_type: entry.eventType,
291‚Üí      severity,
292‚Üí      user_id: entry.userId,
293‚Üí      details: {
