295‚Üí        metadata: entry.metadata,
296‚Üí        error: entry.errorDetails,
297‚Üí        ip_address: entry.ipAddress,
298‚Üí        user_agent: entry.userAgent,
299‚Üí      },
300‚Üí      ip_address: entry.ipAddress,
301‚Üí      created_at: timestamp,
302‚Üí    };
303‚Üí
304‚Üí    await adminApolloClient.mutate({
