306‚Üí      variables: { object: securityEvent },
307‚Üí    });
308‚Üí  }
309‚Üí
310‚Üí  /**
311‚Üí   * Log general audit events
312‚Üí   */
313‚Üí  private async logAuditEvent(entry: SOC2LogEntry, timestamp: string): Promise<void> {
314‚Üí    const auditLog = {
315‚Üí      event_time: timestamp,
316‚Üí      user_id: entry.userId,
317‚Üí      user_email: entry.userEmail,
318‚Üí      user_role: entry.userRole,
319‚Üí      action: entry.eventType,
320‚Üí      resource_type: entry.entityType,
321‚Üí      resource_id: entry.entityId,
322‚Üí      ip_address: entry.ipAddress,
323‚Üí      user_agent: entry.userAgent,
324‚Üí      session_id: entry.sessionId,
325‚Üí      request_id: entry.requestId,
326‚Üí      success: !entry.errorDetails,
327‚Üí      error_message: entry.errorDetails?.message,
328‚Üí      metadata: {
329‚Üí        ...entry.metadata,
330‚Üí        level: entry.level,
331‚Üí        category: entry.category,
332‚Üí        message: entry.message,
333‚Üí        performance: entry.performanceMetrics,
334‚Üí      },
335‚Üí    };
336‚Üí
337‚Üí    await adminApolloClient.mutate({
338‚Üí      mutation: INSERT_AUDIT_LOG,
339‚Üí      variables: { object: auditLog },
340‚Üí    });
341‚Üí  }
342‚Üí
343‚Üí  /**
344‚Üí   * Handle critical events that need immediate attention
345‚Üí   */
346‚Üí  private async handleCriticalEvent(entry: SOC2LogEntry): Promise<void> {
347‚Üí    // In production, this would:
348‚Üí    // 1. Send alerts to security team
349‚Üí    // 2. Trigger incident response workflow
350‚Üí    // 3. Create PagerDuty incident
351‚Üí    // 4. Send to SIEM system
352‚Üí    
353‚Üí    console.error("[CRITICAL EVENT]", {
354‚Üí      timestamp: new Date().toISOString(),
355‚Üí      event: entry.eventType,
356‚Üí      user: entry.userId,
357‚Üí      message: entry.message,
358‚Üí      metadata: entry.metadata,
359‚Üí    });
360‚Üí  }
361‚Üí
362‚Üí  /**
363‚Üí   * Console logging for development
364‚Üí   */
365‚Üí  private consoleLog(entry: SOC2LogEntry, timestamp: string): void {
366‚Üí    const logData = {
367‚Üí      timestamp,
368‚Üí      level: entry.level,
369‚Üí      category: entry.category,
370‚Üí      event: entry.eventType,
371‚Üí      message: entry.message,
372‚Üí      user: entry.userId,
373‚Üí      entity: entry.entityType ? `${entry.entityType}:${entry.entityId}` : undefined,
374‚Üí      metadata: entry.metadata,
375‚Üí      performance: entry.performanceMetrics,
376‚Üí      error: entry.errorDetails,
377‚Üí    };
378‚Üí
379‚Üí    switch (entry.level) {
380‚Üí      case LogLevel.DEBUG:
381‚Üí        console.debug("[SOC2]", logData);
382‚Üí        break;
383‚Üí      case LogLevel.INFO:
384‚Üí        console.info("[SOC2]", logData);
385‚Üí        break;
386‚Üí      case LogLevel.WARNING:
387‚Üí        console.warn("[SOC2]", logData);
388‚Üí        break;
389‚Üí      case LogLevel.ERROR:
390‚Üí      case LogLevel.CRITICAL:
391‚Üí      case LogLevel.SECURITY:
392‚Üí        console.error("[SOC2]", logData);
393‚Üí        break;
394‚Üí      default:
395‚Üí        console.log("[SOC2]", logData);
396‚Üí    }
397‚Üí  }
398‚Üí
399‚Üí  /**
400‚Üí   * Extract client IP from request
401‚Üí   */
402‚Üí  private getClientIP(request: NextRequest): string {
403‚Üí    return (
404‚Üí      request.headers.get("x-forwarded-for")?.split(",")[0] ||
405‚Üí      request.headers.get("x-real-ip") ||
406‚Üí      request.headers.get("cf-connecting-ip") ||
407‚Üí      "unknown"
408‚Üí    );
409‚Üí  }
410‚Üí
411‚Üí  /**
412‚Üí   * Map log level to severity
413‚Üí   */
414‚Üí  private mapLogLevelToSeverity(level: LogLevel): string {
415‚Üí    switch (level) {
416‚Üí      case LogLevel.DEBUG:
417‚Üí      case LogLevel.INFO:
418‚Üí        return "info";
419‚Üí      case LogLevel.WARNING:
420‚Üí        return "warning";
421‚Üí      case LogLevel.ERROR:
422‚Üí        return "error";
423‚Üí      case LogLevel.CRITICAL:
424‚Üí      case LogLevel.SECURITY:
425‚Üí        return "critical";
426‚Üí      default:
427‚Üí        return "info";
428‚Üí    }
429‚Üí  }
430‚Üí
431‚Üí  /**
432‚Üí   * Start buffer flush interval
433‚Üí   */
434‚Üí  private startFlushInterval(): void {
435‚Üí    this.flushInterval = setInterval(() => {
436‚Üí      this.flushBuffer().catch(console.error);
437‚Üí    }, this.flushIntervalMs);
438‚Üí  }
439‚Üí
440‚Üí  /**
441‚Üí   * Flush buffered logs
442‚Üí   */
443‚Üí  private async flushBuffer(): Promise<void> {
444‚Üí    if (this.buffer.length === 0) return;
445‚Üí
446‚Üí    const logsToFlush = [...this.buffer];
447‚Üí    this.buffer = [];
448‚Üí
449‚Üí    for (const entry of logsToFlush) {
450‚Üí      try {
451‚Üí        await this.log(entry);
452‚Üí      } catch (error) {
453‚Üí        console.error("[SOC2Logger] Failed to flush buffered log:", error);
454‚Üí      }
455‚Üí    }
456‚Üí  }
457‚Üí
458‚Üí  /**
459‚Üí   * Cleanup on shutdown
460‚Üí   */
461‚Üí  async shutdown(): Promise<void> {
462‚Üí    if (this.flushInterval) {
463‚Üí      clearInterval(this.flushInterval);
464‚Üí    }
465‚Üí    await this.flushBuffer();
466‚Üí  }
467‚Üí}
468‚Üí
