531‚Üí// Decorator for automatic method logging
532‚Üíexport function SOC2Logged(
533‚Üí  eventType: SOC2EventType,
534‚Üí  category: LogCategory = LogCategory.DATA_ACCESS
535‚Üí) {
536‚Üí  return function (
537‚Üí    target: any,
538‚Üí    propertyKey: string,
539‚Üí    descriptor: PropertyDescriptor
540‚Üí  ) {
541‚Üí    const originalMethod = descriptor.value;
542‚Üí
543‚Üí    descriptor.value = async function (...args: any[]) {
544‚Üí      const startTime = Date.now();
545‚Üí      const { userId, sessionClaims } = await auth();
546‚Üí      
547‚Üí      try {
548‚Üí        const result = await originalMethod.apply(this, args);
549‚Üí        
550‚Üí        await soc2Logger.log({
551‚Üí          level: LogLevel.INFO,
552‚Üí          category,
553‚Üí          eventType,
554‚Üí          message: `${propertyKey} completed successfully`,
555‚Üí          userId: userId || undefined,
556‚Üí          userRole: sessionClaims?.metadata?.role,
557‚Üí          metadata: {
558‚Üí            method: propertyKey,
559‚Üí            duration: Date.now() - startTime,
560‚Üí          },
561‚Üí          performanceMetrics: {
562‚Üí            duration: Date.now() - startTime,
563‚Üí          },
564‚Üí        });
565‚Üí        
566‚Üí        return result;
567‚Üí      } catch (error: any) {
568‚Üí        await soc2Logger.log({
569‚Üí          level: LogLevel.ERROR,
570‚Üí          category,
571‚Üí          eventType,
572‚Üí          message: `${propertyKey} failed`,
573‚Üí          userId: userId || undefined,
574‚Üí          userRole: sessionClaims?.metadata?.role,
575‚Üí          errorDetails: {
576‚Üí            message: error.message,
577‚Üí            stack: error.stack,
578‚Üí          },
579‚Üí          performanceMetrics: {
580‚Üí            duration: Date.now() - startTime,
581‚Üí          },
582‚Üí        });
583‚Üí        
584‚Üí        throw error;
585‚Üí      }
586‚Üí    };
587‚Üí
588‚Üí    return descriptor;
589‚Üí  };
590‚Üí}

