 28‚Üíexport function isPermissionError(error: any): boolean {
 29‚Üí  if (!error?.message) return false;
 30‚Üí
 31‚Üí  const message = error.message.toLowerCase();
 32‚Üí  return (
 33‚Üí    (message.includes("field") && message.includes("not found in type")) ||
 34‚Üí    message.includes("insufficient permissions") ||
 35‚Üí    message.includes("access denied") ||
 36‚Üí    message.includes("forbidden")
 37‚Üí  );
 38‚Üí}
