 40‚Üíexport function parsePermissionError(error: any): GraphQLPermissionError {
 41‚Üí  const message = error.message || "";
 42‚Üí
 43‚Üí  // Extract field name from "field 'field_name' not found in type: 'table_name'"
 44‚Üí  const fieldMatch = message.match(
 45‚Üí    /field '([^']+)' not found in type: '([^']+)'/
 46‚Üí  );
 47‚Üí
 48‚Üí  return {
 49‚Üí    message,
 50‚Üí    field: fieldMatch?.[1],
 51‚Üí    table: fieldMatch?.[2],
 52‚Üí    role: undefined, // Could be extracted from context if available
 53‚Üí  };
 54‚Üí}
