 56‚Üíexport function getPermissionErrorMessage(
 57‚Üí  error: GraphQLPermissionError
 58‚Üí): string {
 59‚Üí  if (error.field && error.table) {
 60‚Üí    return `You don't have permission to access the '${error.field}' field on ${error.table}. Contact your administrator to request access.`;
 61‚Üí  }
 62‚Üí
 63‚Üí  if (error.message.includes("insufficient permissions")) {
 64‚Üí    return "You don't have sufficient permissions to perform this action. Contact your administrator for access.";
 65‚Üí  }
 66‚Üí
 67‚Üí  return "Access denied. You may not have the required permissions for this resource.";
 68‚Üí}
