 70‚Üíexport function handlePermissionError(
 71‚Üí  error: GraphQLPermissionError,
 72‚Üí  context?: string
 73‚Üí): void {
 74‚Üí  const userMessage = getPermissionErrorMessage(error);
 75‚Üí  const errorKey = `permission_${error.field}_${error.table}`;
 76‚Üí
