 791‚Üí### Webhook Handler for User Synchronisation
 792‚Üí
 793‚ÜíCreate a comprehensive webhook handler that syncs Clerk users with your database:
 794‚Üí
 795‚Üí```typescript
 796‚Üí// app/api/webhooks/clerk/route.ts
 797‚Üíimport { Webhook } from "svix";
 798‚Üíimport { headers } from "next/headers";
 799‚Üíimport { WebhookEvent } from "@clerk/nextjs/server";
 800‚Üíimport { db } from "@/lib/db"; // Your database connection
 801‚Üíimport { sql } from "@/lib/sql"; // SQL query builder
 802‚Üí
 803‚Üíinterface UserEventData {
 804‚Üí  id: string;
 805‚Üí  email_addresses: Array<{ email_address: string; id: string }>;
 806‚Üí  first_name: string | null;
 807‚Üí  last_name: string | null;
 808‚Üí  image_url: string | null;
 809‚Üí  username: string | null;
 810‚Üí  public_metadata: Record<string, any>;
 811‚Üí  created_at: number;
 812‚Üí  updated_at: number;
 813‚Üí}
 814‚Üí
 815‚Üí// Webhook processing table for idempotency
 816‚Üíconst ensureIdempotency = async (webhookId: string): Promise<boolean> => {
 817‚Üí  try {
 818‚Üí    await db.execute(sql`
 819‚Üí      INSERT INTO processed_webhooks (id, processed_at)
 820‚Üí      VALUES (${webhookId}, NOW())
 821‚Üí    `);
 822‚Üí    return true;
 823‚Üí  } catch (error) {
 824‚Üí    // Webhook already processed
 825‚Üí    return false;
 826‚Üí  }
 827‚Üí};
 828‚Üí
 829‚Üíexport async function POST(req: Request) {
 830‚Üí  const WEBHOOK_SECRET = process.env.CLERK_WEBHOOK_SECRET;
 831‚Üí
 832‚Üí  if (!WEBHOOK_SECRET) {
 833‚Üí    throw new Error(
 834‚Üí      "Please add CLERK_WEBHOOK_SECRET to your environment variables"
 835‚Üí    );
 836‚Üí  }
 837‚Üí
 838‚Üí  // Get headers
 839‚Üí  const headerPayload = headers();
 840‚Üí  const svix_id = headerPayload.get("svix-id");
 841‚Üí  const svix_timestamp = headerPayload.get("svix-timestamp");
 842‚Üí  const svix_signature = headerPayload.get("svix-signature");
 843‚Üí
 844‚Üí  if (!svix_id || !svix_timestamp || !svix_signature) {
 845‚Üí    return new Response("Error occured -- no svix headers", { status: 400 });
 846‚Üí  }
 847‚Üí
 848‚Üí  // Check idempotency
 849‚Üí  const isNewWebhook = await ensureIdempotency(svix_id);
 850‚Üí  if (!isNewWebhook) {
 851‚Üí    return new Response("Webhook already processed", { status: 200 });
 852‚Üí  }
 853‚Üí
 854‚Üí  // Get body
 855‚Üí  const payload = await req.text();
 856‚Üí  const body = JSON.parse(payload);
 857‚Üí
 858‚Üí  // Verify webhook
 859‚Üí  const wh = new Webhook(WEBHOOK_SECRET);
 860‚Üí  let evt: WebhookEvent;
 861‚Üí
 862‚Üí  try {
 863‚Üí    evt = wh.verify(payload, {
 864‚Üí      "svix-id": svix_id,
 865‚Üí      "svix-timestamp": svix_timestamp,
 866‚Üí      "svix-signature": svix_signature,
 867‚Üí    }) as WebhookEvent;
 868‚Üí  } catch (err) {
 869‚Üí    console.error("Error verifying webhook:", err);
 870‚Üí    return new Response("Error occurred", { status: 400 });
 871‚Üí  }
 872‚Üí
 873‚Üí  // Handle different event types
 874‚Üí  const eventType = evt.type;
 875‚Üí
 876‚Üí  switch (eventType) {
 877‚Üí    case "user.created":
 878‚Üí      await handleUserCreated(evt.data as UserEventData);
 879‚Üí      break;
 880‚Üí    case "user.updated":
 881‚Üí      await handleUserUpdated(evt.data as UserEventData);
 882‚Üí      break;
 883‚Üí    case "user.deleted":
 884‚Üí      await handleUserDeleted(evt.data);
 885‚Üí      break;
 886‚Üí    default:
 887‚Üí      console.log(`Unhandled webhook type: ${eventType}`);
 888‚Üí  }
 889‚Üí
 890‚Üí  return new Response("", { status: 200 });
 891‚Üí}
 892‚Üí
 893‚Üíasync function handleUserCreated(userData: UserEventData) {
 894‚Üí  const primaryEmail = userData.email_addresses[0]?.email_address;
 895‚Üí  const fullName =
 896‚Üí    [userData.first_name, userData.last_name].filter(Boolean).join(" ") || null;
 897‚Üí
 898‚Üí  await db.transaction(async (tx) => {
 899‚Üí    // Create user
 900‚Üí    const [user] = await tx.execute(sql`
 901‚Üí      INSERT INTO users (
 902‚Üí        clerk_user_id, name, email, username, image, role, created_at, updated_at
 903‚Üí      ) VALUES (
 904‚Üí        ${userData.id},
 905‚Üí        ${fullName},
 906‚Üí        ${primaryEmail},
 907‚Üí        ${userData.username},
 908‚Üí        ${userData.image_url},
 909‚Üí        ${userData.public_metadata.role || "viewer"},
 910‚Üí        ${new Date(userData.created_at)},
 911‚Üí        ${new Date(userData.updated_at)}
 912‚Üí      )
 913‚Üí      ON CONFLICT (clerk_user_id) DO UPDATE SET
 914‚Üí        name = EXCLUDED.name,
 915‚Üí        email = EXCLUDED.email,
 916‚Üí        username = EXCLUDED.username,
 917‚Üí        image = EXCLUDED.image,
 918‚Üí        updated_at = EXCLUDED.updated_at
 919‚Üí      RETURNING id, role
 920‚Üí    `);
 921‚Üí
 922‚Üí    // Assign role in junction table
 923‚Üí    const [roleRecord] = await tx.execute(sql`
 924‚Üí      SELECT id FROM roles WHERE name = ${user.role}
 925‚Üí    `);
 926‚Üí
 927‚Üí    if (roleRecord) {
 928‚Üí      await tx.execute(sql`
 929‚Üí        INSERT INTO user_roles (user_id, role_id)
 930‚Üí        VALUES (${user.id}, ${roleRecord.id})
 931‚Üí        ON CONFLICT (user_id, role_id) DO NOTHING
 932‚Üí      `);
 933‚Üí    }
 934‚Üí  });
 935‚Üí}
