 979‚Üíconst isAdminRoute = createRouteMatcher(["/admin(.*)", "/api/admin(.*)"]);
 980‚Üí
 981‚Üíconst isManagerRoute = createRouteMatcher(["/manage(.*)", "/api/manage(.*)"]);
 982‚Üí
 983‚Üíexport default clerkMiddleware(async (auth, req) => {
 984‚Üí  // Public routes don't require authentication
 985‚Üí  if (isPublicRoute(req)) return;
 986‚Üí
 987‚Üí  // Get authentication state
 988‚Üí  const { userId, sessionClaims, protect } = await auth();
