 993‚Üí  }
 994‚Üí
 995‚Üí  // Check role-based access
 996‚Üí  const userRole = sessionClaims?.metadata?.role;
 997‚Üí
 998‚Üí  if (isAdminRoute(req)) {
 999‚Üí    if (!["admin", "org_admin"].includes(userRole)) {
1000‚Üí      return new Response("Forbidden", { status: 403 });
1001‚Üí    }
1002‚Üí  }
1003‚Üí
1004‚Üí  if (isManagerRoute(req)) {
1005‚Üí    if (!["manager", "admin", "org_admin"].includes(userRole)) {
1006‚Üí      return new Response("Forbidden", { status: 403 });
1007‚Üí    }
1008‚Üí  }
1009‚Üí});
