1441‚Üí## Hasura GraphQL Integration
1442‚Üí
1443‚Üí### Apollo Client Setup with Authentication
1444‚Üí
1445‚Üí```typescript
1446‚Üí// lib/apollo-client.ts
1447‚Üí"use client";
1448‚Üíimport { ApolloClient, InMemoryCache, HttpLink, from } from "@apollo/client";
1449‚Üíimport { setContext } from "@apollo/client/link/context";
1450‚Üíimport { onError } from "@apollo/client/link/error";
1451‚Üíimport { useAuth } from "@clerk/nextjs";
1452‚Üíimport { useMemo } from "react";
1453‚Üí
1454‚Üíexport function useApolloClient() {
1455‚Üí  const { getToken } = useAuth();
1456‚Üí
1457‚Üí  return useMemo(() => {
1458‚Üí    const httpLink = new HttpLink({
1459‚Üí      uri: process.env.NEXT_PUBLIC_HASURA_GRAPHQL_API,
1460‚Üí    });
1461‚Üí
1462‚Üí    const authLink = setContext(async (_, { headers }) => {
1463‚Üí      const token = await getToken({ template: "hasura" });
1464‚Üí
1465‚Üí      return {
1466‚Üí        headers: {
1467‚Üí          ...headers,
1468‚Üí          authorization: token ? `Bearer ${token}` : "",
1469‚Üí        },
1470‚Üí      };
1471‚Üí    });
1472‚Üí
1473‚Üí    const errorLink = onError(
1474‚Üí      ({ graphQLErrors, networkError, operation, forward }) => {
1475‚Üí        if (graphQLErrors) {
1476‚Üí          graphQLErrors.forEach(({ message, locations, path, extensions }) => {
1477‚Üí            // Handle permission errors
1478‚Üí            if (extensions?.code === "access-denied") {
1479‚Üí              console.error("Access denied:", message);
1480‚Üí              // Redirect to unauthorised page or show error
1481‚Üí            }
1482‚Üí          });
1483‚Üí        }
1484‚Üí
1485‚Üí        if (networkError) {
1486‚Üí          console.error("Network error:", networkError);
1487‚Üí        }
1488‚Üí      }
1489‚Üí    );
1490‚Üí
1491‚Üí    return new ApolloClient({
1492‚Üí      link: from([errorLink, authLink, httpLink]),
1493‚Üí      cache: new InMemoryCache(),
1494‚Üí      defaultOptions: {
1495‚Üí        watchQuery: {
1496‚Üí          fetchPolicy: "cache-and-network",
1497‚Üí        },
1498‚Üí      },
1499‚Üí    });
1500‚Üí  }, [getToken]);
1501‚Üí}
1502‚Üí
1503‚Üí// Provider component
1504‚Üíexport function ApolloProviderWrapper({
1505‚Üí  children,
1506‚Üí}: {
1507‚Üí  children: React.ReactNode;
1508‚Üí}) {
1509‚Üí  const client = useApolloClient();
1510‚Üí
1511‚Üí  return <ApolloProvider client={client}>{children}</ApolloProvider>;
1512‚Üí}
1513‚Üí```
