1732‚Üí// app/api/auth/sync/route.ts
1733‚Üíimport { auth } from '@clerk/nextjs/server';
1734‚Üíimport { NextRequest, NextResponse } from 'next/server';
1735‚Üíimport { db } from '@/lib/db';
1736‚Üíimport { sql } from '@/lib/sql';
1737‚Üí
1738‚Üíexport async function POST(req: NextRequest) {
1739‚Üítry {
1740‚Üíconst { userId: authUserId } = await auth();
1741‚Üí
1742‚Üí    if (!authUserId) {
1743‚Üí      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
1744‚Üí    }
1745‚Üí
1746‚Üí    const { clerkUserId, email, name, username, image, role } = await req.json();
1747‚Üí
1748‚Üí    // Verify the user is syncing their own data
1749‚Üí    if (clerkUserId !== authUserId) {
1750‚Üí      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
1751‚Üí    }
1752‚Üí
1753‚Üí    // Create or update user
1754‚Üí    const [user] = await db.execute(sql`
1755‚Üí      INSERT INTO users (
1756‚Üí        clerk_user_id, name, email, username, image, role, created_at, updated_at
1757‚Üí      ) VALUES (
1758‚Üí        ${clerkUserId},
1759‚Üí        ${name},
1760‚Üí        ${email},
1761‚Üí        ${username},
1762‚Üí        ${image},
1763‚Üí        ${role || 'viewer'},
1764‚Üí        NOW(),
1765‚Üí        NOW()
1766‚Üí      )
1767‚Üí      ON CONFLICT (clerk_user_id) DO UPDATE SET
1768‚Üí        name = EXCLUDED.name,
1769‚Üí        email = EXCLUDED.email,
1770‚Üí        username = EXCLUDED.username,
1771‚Üí        image = EXCLUDED.image,
1772‚Üí        updated_at = NOW()
1773‚Üí      RETURNING *
1774‚Üí    `);
1775‚Üí
1776‚Üí    // Assign default role in junction table
1777‚Üí    const [roleRecord] = await db.execute(sql`
1778‚Üí      SELECT id FROM roles WHERE name = ${user.role}
1779‚Üí    `);
1780‚Üí
1781‚Üí    if (roleRecord) {
1782‚Üí      await db.execute(sql`
1783‚Üí        INSERT INTO user_roles (user_id, role_id)
1784‚Üí        VALUES (${user.id}, ${roleRecord.id})
1785‚Üí        ON CONFLICT (user_id, role_id) DO NOTHING
1786‚Üí      `);
1787‚Üí    }
1788‚Üí
1789‚Üí    return NextResponse.json(user);
1790‚Üí
1791‚Üí} catch (error) {
1792‚Üíconsole.error('Error syncing user:', error);
1793‚Üíreturn NextResponse.json({ error: 'Internal server error' }, { status: 500 });
1794‚Üí}
1795‚Üí}
