        rowCount: 1
      }
    }, request);

    console.log('‚úÖ Data access log test completed');

    return NextResponse.json({
      success: true,
      message: "Logging test completed successfully",
      timestamp: new Date().toISOString()
    });

  } catch (error: any) {
    console.error('‚ùå API Logging test failed:', error);

    return NextResponse.json({
      success: false,
      message: "Logging test failed",
      error: {
        name: error.name,
