 16‚Üíinterface SecurityAlert {
 17‚Üí  id: string;
 18‚Üí  type: 'suspicious_pattern' | 'rate_limit_exceeded' | 'auth_failure_spike' | 'unusual_access';
 19‚Üí  severity: 'low' | 'medium' | 'high' | 'critical';
 20‚Üí  route: string;
 21‚Üí  userId?: string;
 22‚Üí  ipAddress: string;
