 26‚Üí
 27‚Üíclass EnhancedRouteMonitor {
 28‚Üí  private static instance: EnhancedRouteMonitor;
 29‚Üí  private routeMetrics = new Map<string, RouteMetrics>();
 30‚Üí  private userActivityCache = new Map<string, { lastActivity: number; requestCount: number }>();
 31‚Üí  private suspiciousIPs = new Set<string>();
 32‚Üí  private alerts: SecurityAlert[] = [];
 33‚Üí  
 34‚Üí  // Rate limiting thresholds
 35‚Üí  private readonly RATE_LIMITS: { [key: string]: { requests: number; window: number } } = {
 36‚Üí    '/api/auth/token': { requests: 10, window: 60000 }, // 10 requests per minute
 37‚Üí    '/api/users': { requests: 50, window: 60000 }, // 50 requests per minute
 38‚Üí    '/api/staff/create': { requests: 5, window: 300000 }, // 5 requests per 5 minutes
 39‚Üí    '/api/audit/compliance-report': { requests: 3, window: 300000 }, // 3 requests per 5 minutes
 40‚Üí    'default': { requests: 100, window: 60000 } // Default: 100 requests per minute
 41‚Üí  };
 42‚Üí
 43‚Üí  // Suspicious patterns
 44‚Üí  private readonly SUSPICIOUS_PATTERNS = {
 45‚Üí    rapidAuthFailures: 5, // 5 auth failures in short time
 46‚Üí    unusualAccessTime: { start: 2, end: 6 }, // Access between 2-6 AM
 47‚Üí    rapidRoleChanges: 3, // 3 role changes in short time
 48‚Üí    bulkDataAccess: 1000 // Accessing more than 1000 records
 49‚Üí  };
 50‚Üí
 51‚Üí  private constructor() {
 52‚Üí    this.startCleanupInterval();
 53‚Üí    this.startAnalyticsInterval();
 54‚Üí  }
 55‚Üí
 56‚Üí  static getInstance(): EnhancedRouteMonitor {
 57‚Üí    if (!EnhancedRouteMonitor.instance) {
 58‚Üí      EnhancedRouteMonitor.instance = new EnhancedRouteMonitor();
 59‚Üí    }
 60‚Üí    return EnhancedRouteMonitor.instance;
 61‚Üí  }
 62‚Üí
 63‚Üí  /**
 64‚Üí   * Monitor a route request
 65‚Üí   */
 66‚Üí  async monitorRequest(
 67‚Üí    request: NextRequest,
 68‚Üí    userId?: string,
 69‚Üí    startTime?: number,
 70‚Üí    success = true,
 71‚Üí    responseData?: any
 72‚Üí  ): Promise<void> {
 73‚Üí    const route = this.normalizeRoute(request.nextUrl.pathname);
 74‚Üí    const ipAddress = this.getClientIP(request);
