153‚Üí
154‚Üíexport class SOC2Logger {
155‚Üí  private static instance: SOC2Logger;
156‚Üí  private buffer: SOC2LogEntry[] = [];
157‚Üí  private flushInterval: NodeJS.Timeout | null = null;
158‚Üí  private readonly maxBufferSize = 100;
159‚Üí  private readonly flushIntervalMs = 5000;
160‚Üí
161‚Üí  private constructor() {
162‚Üí    // Start buffer flush interval
163‚Üí    this.startFlushInterval();
164‚Üí  }
165‚Üí
166‚Üí  static getInstance(): SOC2Logger {
167‚Üí    if (!SOC2Logger.instance) {
168‚Üí      SOC2Logger.instance = new SOC2Logger();
169‚Üí    }
170‚Üí    return SOC2Logger.instance;
171‚Üí  }
172‚Üí
173‚Üí  /**
174‚Üí   * Main logging method
175‚Üí   */
176‚Üí  async log(entry: SOC2LogEntry, request?: NextRequest): Promise<void> {
177‚Üí    try {
178‚Üí      // Enrich entry with request data
179‚Üí      if (request) {
180‚Üí        entry.ipAddress = this.getClientIP(request);
181‚Üí        entry.userAgent = request.headers.get("user-agent") || undefined;
182‚Üí        entry.requestId = request.headers.get("x-request-id") || crypto.randomUUID();
183‚Üí        entry.traceId = request.headers.get("x-trace-id") || undefined;
184‚Üí      }
185‚Üí
186‚Üí      // Add timestamp
187‚Üí      const timestamp = new Date().toISOString();
188‚Üí
189‚Üí      // Determine which table to write to based on category
190‚Üí      switch (entry.category) {
191‚Üí        case LogCategory.AUTHENTICATION:
192‚Üí        case LogCategory.AUTHORIZATION:
193‚Üí          await this.logAuthEvent(entry, timestamp);
194‚Üí          break;
195‚Üí          
196‚Üí        case LogCategory.DATA_ACCESS:
197‚Üí          await this.logDataAccess(entry, timestamp);
198‚Üí          break;
199‚Üí          
200‚Üí        case LogCategory.SECURITY_EVENT:
201‚Üí          await this.logSecurityEvent(entry, timestamp);
202‚Üí          break;
203‚Üí          
204‚Üí        default:
205‚Üí          await this.logAuditEvent(entry, timestamp);
206‚Üí      }
207‚Üí
208‚Üí      // Also log to console in development
209‚Üí      if (process.env.NODE_ENV === "development") {
210‚Üí        this.consoleLog(entry, timestamp);
211‚Üí      }
212‚Üí
213‚Üí      // Check for critical events that need immediate attention
214‚Üí      if (entry.level === LogLevel.CRITICAL || entry.level === LogLevel.SECURITY) {
215‚Üí        await this.handleCriticalEvent(entry);
216‚Üí      }
217‚Üí    } catch (error) {
218‚Üí      // Never fail silently - log to console as fallback
219‚Üí      console.error("[SOC2Logger] Failed to log entry:", error, entry);
220‚Üí      
221‚Üí      // Add to buffer for retry
222‚Üí      this.buffer.push(entry);
223‚Üí      
224‚Üí      // Flush buffer if it's getting full
225‚Üí      if (this.buffer.length >= this.maxBufferSize) {
226‚Üí        await this.flushBuffer();
227‚Üí      }
228‚Üí    }
229‚Üí  }
230‚Üí
231‚Üí  /**
232‚Üí   * Log authentication/authorization events
233‚Üí   */
234‚Üí  private async logAuthEvent(entry: SOC2LogEntry, timestamp: string): Promise<void> {
235‚Üí    const authEvent = {
236‚Üí      event_time: timestamp,
237‚Üí      event_type: entry.eventType,
238‚Üí      user_id: entry.userId,
239‚Üí      user_email: entry.userEmail,
240‚Üí      ip_address: entry.ipAddress,
241‚Üí      user_agent: entry.userAgent,
242‚Üí      success: !entry.errorDetails,
243‚Üí      failure_reason: entry.errorDetails?.message,
244‚Üí      metadata: {
245‚Üí        ...entry.metadata,
246‚Üí        session_id: entry.sessionId,
247‚Üí        request_id: entry.requestId,
248‚Üí        trace_id: entry.traceId,
249‚Üí      },
250‚Üí    };
251‚Üí
252‚Üí    await adminApolloClient.mutate({
253‚Üí      mutation: INSERT_AUTH_EVENT,
254‚Üí      variables: { object: authEvent },
255‚Üí    });
256‚Üí  }
257‚Üí
258‚Üí  /**
259‚Üí   * Log data access events
260‚Üí   */
261‚Üí  private async logDataAccess(entry: SOC2LogEntry, timestamp: string): Promise<void> {
262‚Üí    const dataAccessLog = {
263‚Üí      accessed_at: timestamp,
264‚Üí      user_id: entry.userId,
265‚Üí      resource_type: entry.entityType,
266‚Üí      resource_id: entry.entityId,
267‚Üí      access_type: entry.eventType,
268‚Üí      data_classification: entry.metadata?.dataClassification || "MEDIUM",
269‚Üí      fields_accessed: entry.metadata?.fields,
270‚Üí      query_executed: entry.metadata?.query,
271‚Üí      row_count: entry.metadata?.rowCount,
272‚Üí      ip_address: entry.ipAddress,
273‚Üí      session_id: entry.sessionId,
274‚Üí      metadata: entry.metadata,
275‚Üí    };
276‚Üí
277‚Üí    await adminApolloClient.mutate({
278‚Üí      mutation: INSERT_DATA_ACCESS_LOG,
279‚Üí      variables: { object: dataAccessLog },
280‚Üí    });
281‚Üí  }
282‚Üí
283‚Üí  /**
284‚Üí   * Log security events
285‚Üí   */
286‚Üí  private async logSecurityEvent(entry: SOC2LogEntry, timestamp: string): Promise<void> {
287‚Üí    const severity = this.mapLogLevelToSeverity(entry.level);
288‚Üí    
289‚Üí    const securityEvent = {
290‚Üí      event_type: entry.eventType,
291‚Üí      severity,
292‚Üí      user_id: entry.userId,
293‚Üí      details: {
294‚Üí        message: entry.message,
295‚Üí        metadata: entry.metadata,
296‚Üí        error: entry.errorDetails,
297‚Üí        ip_address: entry.ipAddress,
298‚Üí        user_agent: entry.userAgent,
299‚Üí      },
300‚Üí      ip_address: entry.ipAddress,
301‚Üí      created_at: timestamp,
302‚Üí    };
303‚Üí
304‚Üí    await adminApolloClient.mutate({
305‚Üí      mutation: INSERT_SECURITY_EVENT,
306‚Üí      variables: { object: securityEvent },
307‚Üí    });
308‚Üí  }
309‚Üí
310‚Üí  /**
311‚Üí   * Log general audit events
312‚Üí   */
313‚Üí  private async logAuditEvent(entry: SOC2LogEntry, timestamp: string): Promise<void> {
314‚Üí    const auditLog = {
315‚Üí      event_time: timestamp,
316‚Üí      user_id: entry.userId,
317‚Üí      user_email: entry.userEmail,
318‚Üí      user_role: entry.userRole,
319‚Üí      action: entry.eventType,
320‚Üí      resource_type: entry.entityType,
321‚Üí      resource_id: entry.entityId,
322‚Üí      ip_address: entry.ipAddress,
323‚Üí      user_agent: entry.userAgent,
324‚Üí      session_id: entry.sessionId,
325‚Üí      request_id: entry.requestId,
326‚Üí      success: !entry.errorDetails,
327‚Üí      error_message: entry.errorDetails?.message,
328‚Üí      metadata: {
329‚Üí        ...entry.metadata,
330‚Üí        level: entry.level,
331‚Üí        category: entry.category,
332‚Üí        message: entry.message,
333‚Üí        performance: entry.performanceMetrics,
334‚Üí      },
335‚Üí    };
336‚Üí
337‚Üí    await adminApolloClient.mutate({
338‚Üí      mutation: INSERT_AUDIT_LOG,
339‚Üí      variables: { object: auditLog },
340‚Üí    });
341‚Üí  }
342‚Üí
343‚Üí  /**
344‚Üí   * Handle critical events that need immediate attention
345‚Üí   */
346‚Üí  private async handleCriticalEvent(entry: SOC2LogEntry): Promise<void> {
347‚Üí    // In production, this would:
348‚Üí    // 1. Send alerts to security team
349‚Üí    // 2. Trigger incident response workflow
350‚Üí    // 3. Create PagerDuty incident
351‚Üí    // 4. Send to SIEM system
352‚Üí    
353‚Üí    console.error("[CRITICAL EVENT]", {
354‚Üí      timestamp: new Date().toISOString(),
355‚Üí      event: entry.eventType,
356‚Üí      user: entry.userId,
357‚Üí      message: entry.message,
358‚Üí      metadata: entry.metadata,
359‚Üí    });
360‚Üí  }
361‚Üí
362‚Üí  /**
363‚Üí   * Console logging for development
364‚Üí   */
365‚Üí  private consoleLog(entry: SOC2LogEntry, timestamp: string): void {
366‚Üí    const logData = {
367‚Üí      timestamp,
368‚Üí      level: entry.level,
369‚Üí      category: entry.category,
370‚Üí      event: entry.eventType,
371‚Üí      message: entry.message,
372‚Üí      user: entry.userId,
373‚Üí      entity: entry.entityType ? `${entry.entityType}:${entry.entityId}` : undefined,
374‚Üí      metadata: entry.metadata,
375‚Üí      performance: entry.performanceMetrics,
376‚Üí      error: entry.errorDetails,
377‚Üí    };
378‚Üí
379‚Üí    switch (entry.level) {
380‚Üí      case LogLevel.DEBUG:
381‚Üí        console.debug("[SOC2]", logData);
382‚Üí        break;
383‚Üí      case LogLevel.INFO:
384‚Üí        console.info("[SOC2]", logData);
385‚Üí        break;
386‚Üí      case LogLevel.WARNING:
387‚Üí        console.warn("[SOC2]", logData);
388‚Üí        break;
389‚Üí      case LogLevel.ERROR:
390‚Üí      case LogLevel.CRITICAL:
391‚Üí      case LogLevel.SECURITY:
392‚Üí        console.error("[SOC2]", logData);
393‚Üí        break;
394‚Üí      default:
395‚Üí        console.log("[SOC2]", logData);
396‚Üí    }
397‚Üí  }
398‚Üí
399‚Üí  /**
400‚Üí   * Extract client IP from request
401‚Üí   */
402‚Üí  private getClientIP(request: NextRequest): string {
403‚Üí    return (
404‚Üí      request.headers.get("x-forwarded-for")?.split(",")[0] ||
405‚Üí      request.headers.get("x-real-ip") ||
406‚Üí      request.headers.get("cf-connecting-ip") ||
407‚Üí      "unknown"
408‚Üí    );
409‚Üí  }
410‚Üí
411‚Üí  /**
412‚Üí   * Map log level to severity
413‚Üí   */
414‚Üí  private mapLogLevelToSeverity(level: LogLevel): string {
415‚Üí    switch (level) {
416‚Üí      case LogLevel.DEBUG:
417‚Üí      case LogLevel.INFO:
418‚Üí        return "info";
419‚Üí      case LogLevel.WARNING:
420‚Üí        return "warning";
421‚Üí      case LogLevel.ERROR:
422‚Üí        return "error";
423‚Üí      case LogLevel.CRITICAL:
424‚Üí      case LogLevel.SECURITY:
425‚Üí        return "critical";
426‚Üí      default:
427‚Üí        return "info";
428‚Üí    }
429‚Üí  }
430‚Üí
431‚Üí  /**
432‚Üí   * Start buffer flush interval
433‚Üí   */
434‚Üí  private startFlushInterval(): void {
435‚Üí    this.flushInterval = setInterval(() => {
436‚Üí      this.flushBuffer().catch(console.error);
437‚Üí    }, this.flushIntervalMs);
438‚Üí  }
439‚Üí
440‚Üí  /**
441‚Üí   * Flush buffered logs
442‚Üí   */
443‚Üí  private async flushBuffer(): Promise<void> {
444‚Üí    if (this.buffer.length === 0) return;
445‚Üí
446‚Üí    const logsToFlush = [...this.buffer];
447‚Üí    this.buffer = [];
448‚Üí
449‚Üí    for (const entry of logsToFlush) {
450‚Üí      try {
451‚Üí        await this.log(entry);
452‚Üí      } catch (error) {
453‚Üí        console.error("[SOC2Logger] Failed to flush buffered log:", error);
454‚Üí      }
455‚Üí    }
456‚Üí  }
457‚Üí
458‚Üí  /**
459‚Üí   * Cleanup on shutdown
460‚Üí   */
461‚Üí  async shutdown(): Promise<void> {
462‚Üí    if (this.flushInterval) {
463‚Üí      clearInterval(this.flushInterval);
464‚Üí    }
465‚Üí    await this.flushBuffer();
466‚Üí  }
467‚Üí}
