  3‚Üíimport { adminApolloClient } from "@/lib/server-apollo-client";
  4‚Üíimport { gql } from "@apollo/client";
  5‚Üíimport {
  6‚Üí  getUserPermissions,
  7‚Üí  canAssignRole,
  8‚Üí  UserRole,
  9‚Üí  updateUserRole,
 10‚Üí} from "@/lib/user-sync";
 11‚Üí
 12‚Üí// Get user by ID (either database ID or Clerk ID)
 13‚Üíconst GET_USER_BY_ID = gql`
