 36‚Üí      }
 37‚Üí    }
 38‚Üí  }
 39‚Üí`;
 40‚Üí
 41‚Üíconst GET_USER_BY_CLERK_ID = gql`
 42‚Üí  query GetUserByClerkId($clerkId: String!) {
 43‚Üí    users(where: { clerk_user_id: { _eq: $clerkId } }) {
 44‚Üí      id
 45‚Üí      name
